INSERT INTO `book` (`id`, `name`, `description`, `price`, `quantity`, `special`, `sale_off`, `picture`, `created`, `created_by`, `modified`, `modified_by`, `status`, `ordering`, `category_id`) VALUES
(12, '42 năm làm ăn tại Mỹ và Trung Quốc', '<p>Cuốn s&aacute;ch được viết từ những kinh nghiệm xương m&aacute;u của Alan Phan trong những năm d&agrave;i lăn lộn ở nước ngo&agrave;i. S&aacute;ch đ&uacute;c kết những b&agrave;i học được &aacute;p dụng linh hoạt từ l&yacute; thuyết đến thực tiễn để l&yacute; giải th&agrave;nh c&ocirc;ng của doanh nghiệp.</p>\r\n\r\n<p>Những th&agrave;nh c&ocirc;ng v&agrave; thất bại của ch&iacute;nh&nbsp;TS. Alan Phan được đưa ra mổ xẻ, ph&acirc;n t&iacute;ch trong những trang s&aacute;ch gi&uacute;p người đọc hiểu r&otilde; hơn về những yếu tố th&agrave;nh c&ocirc;ng trong doanh nghiệp m&agrave; rủi ro l&agrave; một phần tất yếu.&nbsp;</p>\r\n\r\n<p>Cuốn s&aacute;ch l&agrave; những chia sẻ, ph&acirc;n t&iacute;ch mang t&iacute;nh gợi mở, rất thực tế v&agrave; hữu &iacute;ch về kinh doanh tại Mỹ v&agrave; Trung Quốc của doanh nh&acirc;n, tiến sĩ Alan Phan trong khoảng thời gian 42 năm. Với c&aacute;ch viết giản dị nhưng d&iacute; dỏm, s&acirc;u sắc, t&aacute;c giả đ&atilde; phản &aacute;nh những vấn đề tưởng như rất vĩ m&ocirc;: kinh tế Mỹ, kinh tế Trung Quốc, nguồn vốn, chế độ ph&aacute;p l&yacute;&hellip; trở n&ecirc;n gần gũi thiết thực hơn.</p>\r\n', '199000', 8, 0, 5, '3s4aiq06.png', '2020-11-09', 'admin', '2020-12-10', 'admin', 1, 1, 11),
(13, 'Bí mật Phan Thiên Ân ', '<p><strong>B&iacute; mật của Phan Thi&ecirc;n &Acirc;n</strong>&nbsp;được ra đời v&agrave;o năm 2015, s&aacute;ch được ch&iacute;nh tiến sĩ Alan Phan chắp b&uacute;t &amp; ph&oacute;ng t&aacute;c lại theo nguy&ecirc;n t&aacute;c của cuốn s&aacute;ch v&ocirc; gi&aacute; theo thời gian &ldquo;Người b&aacute;n h&agrave;ng vĩ đại nhất thế giới&rdquo; của Og Mandino.</p>\r\n\r\n<p>L&uacute;c sinh thời TS. Alan Phan c&oacute; lần từng chia sẻ rằng lớn l&ecirc;n, ra đời, sau bao nhi&ecirc;u b&agrave;i học về tủi nhục, ngh&egrave;o h&egrave;n, thua k&eacute;m v&agrave; thất bại &ocirc;ng bắt đầu nhận ra gi&aacute; trị của đồng tiền. Ch&iacute;nh v&igrave; vậy &ocirc;ng quyết định cho in lại cuốn s&aacute;ch n&agrave;y với mong muốn lớp trẻ đi sau tr&aacute;nh được những lỗi lầm v&agrave; r&uacute;t ngắn qu&atilde;ng đường đi t&igrave;m sự gi&agrave;u sang về vật chất lẫn t&acirc;m hồn.</p>\r\n\r\n<p>Bằng ng&ograve;i b&uacute;t đặc trưng của Alan Phan được mệnh danh l&agrave; &ldquo;đầy t&iacute;nh triết l&yacute; nh&acirc;n sinh&rdquo; của một &ldquo;triết gia doanh nh&acirc;n&rdquo;. &Ocirc;ng đ&atilde; đưa ch&uacute;ng ta đi một h&agrave;nh tr&igrave;nh đủ để hiểu rằng &ldquo;Con đường để đạt được th&agrave;nh c&ocirc;ng v&agrave; gi&agrave;u c&oacute; đều c&oacute; những thử th&aacute;ch. V&agrave; để th&agrave;nh c&ocirc;ng hay gi&agrave;u c&oacute; cần phải c&oacute; kinh nghiệm v&agrave; b&iacute; quyết.</p>\r\n\r\n<p>Với lối viết dung dị, đơn giản: d&ugrave;ng một c&acirc;u chuyện với bối cảnh th&acirc;n thuộc v&agrave; mười tờ kinh để thắp n&ecirc;n những b&agrave;i học thay đổi cuộc đời &ndash; tất cả sẽ l&agrave;m n&ecirc;n những sự thay đổi &ldquo;diệu k&igrave;&rdquo; khi ta &aacute;p dụng triệt để.</p>\r\n\r\n<p>Trong cuốn s&aacute;ch người đọc sẽ t&igrave;m thấy nhiều bất ngờ th&uacute; vị về việc kh&aacute;m ph&aacute; ch&iacute;nh bản th&acirc;n v&agrave; sức mạnh nội tại của m&igrave;nh qua ng&ograve;i b&uacute;t &ldquo;kh&ocirc;ng-kinh-tế&rdquo; v&agrave; &ldquo;đầy t&iacute;nh triết l&yacute; nh&acirc;n sinh&rdquo; của một &ldquo;triết gia doanh nh&acirc;n&rdquo; bậc thầy Alan Phan.</p>\r\n\r\n<p>&ldquo;Con đường để đạt được th&agrave;nh c&ocirc;ng v&agrave; gi&agrave;u c&oacute; đều c&oacute; những thử th&aacute;ch. V&agrave; để th&agrave;nh c&ocirc;ng hay gi&agrave;u c&oacute; cần phải c&oacute; kinh nghiệm v&agrave; b&iacute; quyết.</p>\r\n\r\n<p>Tất cả những c&ocirc;ng thức th&agrave;nh c&ocirc;ng, hạnh ph&uacute;c, b&igrave;nh an v&agrave; tiền bạc của những người gi&agrave;u nhất thế giới sẽ được g&oacute;i gọn trong cuốn s&aacute;ch n&agrave;y qua 10 tờ kinh. Mỗi tờ kinh l&agrave; những lời chỉ dạy của tiền nh&acirc;n, chứa đựng một nguy&ecirc;n l&yacute;, một định luật, một sự thực căn bản m&agrave; người đọc phải thấu hiểu v&agrave; ghi khắc v&agrave;o tiềm thức. Khi bạn đ&atilde; đưa những nguy&ecirc;n l&yacute; n&agrave;y v&agrave;o tận bản năng, bạn sẽ c&oacute; quyền lực để th&acirc;u lượm tất cả sự gi&agrave;u sang m&agrave; bạn muốn.&rdquo;</p>\r\n\r\n<p><strong>NHỮNG ĐỘC GIẢ N&Agrave;O PH&Ugrave; HỢP VỚI CUỐN B&Iacute; MẬT CỦA PHAN THI&Ecirc;N &Acirc;N</strong></p>\r\n\r\n<p>Tất cả những ai muốn n&acirc;ng tầm tư duy của bản th&acirc;n, muốn biết được rằng ngo&agrave;i kia những tiền nh&acirc;n của ch&uacute;ng ta đ&atilde; từng suy nghĩ như thế n&agrave;o về th&agrave;nh c&ocirc;ng, về nguy&ecirc;n l&yacute; của hạnh ph&uacute;c, b&igrave;nh an, về c&aacute;ch vận h&agrave;nh của tiền bạc. H&atilde;y đọc cuốn s&aacute;ch n&agrave;y.</p>\r\n\r\n<p><strong>Bộ combo l&agrave; m&oacute;n qu&agrave; v&ocirc; c&ugrave;ng &yacute; nghĩa thiết thực tặng đối t&aacute;c, bạn b&egrave;, gia đ&igrave;nh, người th&acirc;n. Cuốn s&aacute;ch nhỏ với khổ cầm tay tiện cho v&agrave;o t&uacute;i x&aacute;ch, balo để đọc h&agrave;ng ng&agrave;y. M&oacute;n qu&agrave; nhỏ nhưng c&oacute; gi&aacute; trị to lớn trong việc thay đổi cuộc đời v&agrave; số phận h&agrave;ng triệu người tr&ecirc;n thế giới.</strong></p>\r\n', '89000', 3, 0, 5, 'sgdb3moh.png', '2020-11-09', 'admin', '2020-12-10', 'admin', 1, 4, 11),
(14, 'Niêm yết sàn Mỹ', '<p>Cuốn s&aacute;ch Ni&ecirc;m yết s&agrave;n Mỹ của t&aacute;c giả Alan Phan sẽ cho bạn hiểu những l&yacute; do tại sao phải ni&ecirc;m yết s&agrave;n mỹ : sẽ gi&uacute;p cho c&ocirc;ng ty bạn c&oacute; một thương hiệu cao v&agrave; uy t&iacute;n hơn s&agrave;n Việt Nam hay s&agrave;n &Aacute; Ch&acirc;u. S&agrave;n Mỹ thường tăng thị gi&aacute; của c&ocirc;ng ty nhờ t&iacute;nh thanh khoản,sự minh bạch, kỷ cương của ban quản l&yacute;, gi&aacute; trị c&aacute;c b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh&hellip;</p>\r\n\r\n<p>C&ocirc;ng ty tr&ecirc;n s&agrave;n Mỹ thường dễ lấy t&agrave;i trợ (funding) từ c&aacute;c quỹ đầu tư khắp thế giới. C&aacute;c đối t&aacute;c chiến lược sẽ dễ d&agrave;ng l&agrave;m việc với một c&ocirc;ng ty l&ecirc;n s&agrave;n Mỹ hơn l&agrave; c&aacute;c nơi kh&aacute;c.</p>\r\n\r\n<p>Cuốn s&aacute;ch Ni&ecirc;m Yết S&agrave;n Mỹ sẽ gi&uacute;p bạn một nhận thức thực tế để quyết định:</p>\r\n\r\n<p>C&ocirc;ng ty bạn c&oacute; đủ điều kiện để l&ecirc;n s&agrave;n Mỹ?</p>\r\n\r\n<p>C&ocirc;ng ty phải t&aacute;i cấu tr&uacute;c thế n&agrave;o để ph&ugrave; hợp với những đ&ograve;i hỏi về kỷ cương, quản trị v&agrave; ph&aacute;p l&yacute;?</p>\r\n\r\n<p>Chiến lược v&agrave; chiến thuật n&agrave;o trong việc b&aacute;n cổ phần để tạo thị gi&aacute; cao?</p>\r\n\r\n<p>Sử dụng lợi thế n&agrave;o để lấy t&agrave;i trợ thật rẻ v&agrave; nhanh ch&oacute;ng cho c&ocirc;ng ty?</p>\r\n\r\n<p>Những kinh nghiệm g&igrave; của người đi trước để c&ocirc;ng ty bạn học hỏi?</p>\r\n', '99000', 4, 0, 5, 'gey4to3i.png', '2020-11-09', 'admin', '2020-12-10', 'admin', 1, 10, 11),
(15, 'Góc nhìn Alan về Xã Hội', 'Tập hợp các viết của TS. Alan Phan về xã hội con người Việt Nam và thế giới. Giúp bạn có được một góc nhìn khách quan và sâu sắc về xã hội con người Việt.', '99000', 5, 0, 5, 'mb3j4oep.png', '2020-11-09', 'admin', '2020-11-10', 'admin', 1, 4, 11),
(16, 'Góc nhìn Alan về Kinh Tế', '<p>S&aacute;ch l&agrave; tập hợp c&aacute;c b&agrave;i viết về kinh tế Việt Nam v&agrave; thế giới, cũng như c&aacute;c b&agrave;i học kinh doanh nhằm cung cấp cho độc giả c&aacute;i nh&igrave;n tổng qu&aacute;t cũng như c&aacute;ch tiếp cận liền mạch g&oacute;c nh&igrave;n về c&aacute;c vấn đề kinh tế của một người c&oacute; hơn 42 năm b&ocirc;n ba v&agrave; kinh doanh khắp năm ch&acirc;u.</p>\r\n\r\n<p>Những nhận định, đ&aacute;nh gi&aacute; của TS. Alan Phan về m&ocirc;i trường kinh doanh l&agrave; những điều r&uacute;t ra từ ch&iacute;nh trải nghiệm của m&igrave;nh. V&igrave; vậy những b&agrave;i viết của &ocirc;ng lu&ocirc;n chứa đựng những th&ocirc;ng tin, b&agrave;i học gi&aacute; trị cho tất cả những người l&agrave;m kinh doanh, đặc biệt alf c&aacute;c doanh nh&acirc;n trẻ Việt Nam tr&ecirc;n đường hội nhập</p>\r\n', '99000', 5, 0, 5, '5ky7zhpa.png', '2020-11-09', 'admin', '2020-12-10', 'admin', 1, 5, 11),
(17, 'Những bài chưa xuất bản', 'Tác phẩm là tập hợp những bài viết đặc biệt cũng như nhiều sản phẩm trí tuệ của tiến sĩ Alan Phan trong hơn mười năm cầm bút, với 7 phần lớn được hệ thống một cách logic: thế giới mới, xã hội Việt, con người Việt, nghĩ ngoài cái hộp…', '101000', 5, 0, 5, 'jb2iza4x.png', '2020-11-09', 'admin', '2020-11-10', 'admin', 1, 9, 11),
(18, 'Góc nhìn Alan - Dành tặng doanh nhân Việt trong thế trận toàn cầu', '<p>Đ&acirc;y c&oacute; thể xem l&agrave; cuốn s&aacute;ch cuối c&ugrave;ng của Alan v&agrave; to&agrave;n diện nhất của TS. Alan Phan. Số liệu rồi sẽ lạc hậu, dự đo&aacute;n tương lai sẽ th&agrave;nh qu&aacute; khứ &ndash; duy chỉ c&oacute; nh&acirc;n sinh quan, triết l&yacute; sống, tinh thần v&agrave; &lsquo;G&oacute;c nh&igrave;n Alan Phan&rsquo; l&agrave; c&ograve;n sống m&atilde;i, vẫn lu&ocirc;n l&agrave; một vi&ecirc;n ngọc qu&yacute;, l&agrave; ngọn hải đăng để nhiều người c&oacute; thể d&otilde;i theo v&agrave; vượt qua gian tru&acirc;n thử th&aacute;ch để t&igrave;m đến mục đ&iacute;ch cuộc đời.</p>\r\n\r\n<p>&ldquo;Nếu một doanh nghiệp đ&atilde; quen thuộc v&agrave; an phận &ldquo;sống&rdquo; trong ng&ocirc;i l&agrave;ng Việt với những th&oacute;i quen in s&acirc;u v&agrave;o văn ho&aacute; của l&atilde;nh đạo v&agrave; c&ocirc;ng ty, th&igrave; g&oacute;c nh&igrave;n của t&aacute;c giả về một &ldquo;chiến trường&rdquo; ngo&agrave;i biển lớn c&oacute; lẽ l&agrave; thừa th&atilde;i.</p>\r\n\r\n<p>Nhưng nếu bạn muốn đối diện với thử th&aacute;ch to&agrave;n cầu, t&igrave;m kiếm những th&agrave;nh tựu mới hơn, lớn hơn&hellip; bạn sẽ phải nghi&ecirc;n khảo lại tầm nh&igrave;n, kỹ năng, cũng như định vị lại c&aacute;c chiến thuật, cơ hội v&agrave; c&aacute;i gi&aacute; phải trả (d&ugrave; th&agrave;nh c&ocirc;ng hay thất bại)&rdquo;.</p>\r\n', '149000', 2, 0, 5, 'cs7t2b91.png', '2020-11-09', 'admin', '2020-12-10', 'admin', 1, 3, 11),
(19, 'Người đàn ông đánh bại mọi thị trường', '<p>Trong lịch sử chỉ c&oacute; duy nhất một người khiến cho c&aacute;c tay chủ s&ograve;ng bạc ở Las Vegas cũng như c&aacute;c nh&agrave; quản l&yacute; quỹ ở Wall Street đều phải k&iacute;nh nể &ndash; đ&oacute; ch&iacute;nh l&agrave; gi&aacute;o sư Edward Othorp!</p>\r\n\r\n<p>Một người vừa l&agrave; gi&aacute;o sư to&aacute;n học, vừa l&agrave; tay cờ bạc h&agrave;ng đầu v&agrave; cũng l&agrave; nh&agrave; đầu tư huyền thoại c&oacute; lẽ chỉ c&oacute; thể tồn tại trong c&aacute;c tiểu thuyết. Tuy nhi&ecirc;n, điều kh&oacute; tin n&agrave;y vẫn xảy ra trong thực tế!</p>\r\n\r\n<p>Edward Thorp</p>\r\n\r\n<p>Khi đang l&agrave; nghi&ecirc;n cứu sinh Tiến sĩ tại Đại học California, Edward Thorp đ&atilde; ph&aacute;t minh ra phương ph&aacute;p đếm b&agrave;i gi&uacute;p cho những người chơi b&igrave;nh thường c&oacute; thể đ&aacute;nh bại nh&agrave; c&aacute;i trong tr&ograve; chơi Blackjack, Roulette, Baccarat. Được truyền cảm hứng bởi Thorp, h&agrave;ng ng&agrave;n người chơi tr&ecirc;n khắp nước Mỹ đ&atilde; gia nhập &ldquo;đội qu&acirc;n&rdquo; đếm b&agrave;i nhằm đ&aacute;nh bại nh&agrave; c&aacute;i.&nbsp;Nghiệp đo&agrave;n Casino ở Mỹ đ&atilde; phải nghi&ecirc;n cứu &ldquo;n&aacute;t &oacute;c&rdquo; để ngăn cản kh&ocirc;ng cho &ocirc;ng v&agrave; &ldquo;đội qu&acirc;n của m&igrave;nh&rdquo; chơi b&agrave;i. Họ l&agrave;m mọi c&aacute;ch để ngăn cản thậm ch&iacute; l&agrave; thay đổi cả luật chơi theo hướng c&oacute; lợi cho nh&agrave; c&aacute;i v&agrave; g&acirc;y kh&oacute; dễ cho những người chơi v&agrave; ch&iacute;nh&nbsp;Thorp cũng bị cấm cửa tại tất cả c&aacute;c s&ograve;ng b&agrave;i nước Mỹ. Nhưng d&ugrave; sao chăng nữa, với ph&aacute;t hiện của Edward Thorp, ng&agrave;nh casino cũng đ&atilde; thay đổi m&atilde;i m&atilde;i.</p>\r\n\r\n<p>V&agrave;o năm 1967, Edward Thorp quyết định chuyển to&agrave;n bộ sự tập trung sang &ldquo;s&ograve;ng b&agrave;i lớn nhất tr&ecirc;n thế giới&rdquo;: Phố Wall. Tại đ&acirc;y, Thorp đ&atilde; c&oacute; những ph&aacute;t kiến tr&ecirc;n cơ sở vận dụng kiến thức về x&aacute;c suất học thống k&ecirc; trong to&aacute;n học v&agrave; thực hiện những ph&aacute;t triển của m&igrave;nh để đ&aacute;nh bại thị trường. Ch&iacute;nh &ocirc;ng đ&atilde; mở ra một kỷ nguy&ecirc;n của c&aacute;c quỹ đầu tư định lượng như ch&uacute;ng ta đ&atilde; thấy ng&agrave;y nay. &Ocirc;ng được mệnh danh l&agrave; &ldquo;Bố Gi&agrave;&rdquo; của phương ph&aacute;p đầu tư định lượng v&agrave; l&agrave; người bạn chơi b&agrave;i Bridge th&acirc;n thiết của Warren Buffett.</p>\r\n\r\n<p>Trong cuốn&nbsp;<strong><em>&ldquo;Người Đ&agrave;n &Ocirc;ng Đ&aacute;nh Bại Mọi Thị Trường&rdquo; (tựa gốc&nbsp;A man for all markets: from Las Vegas to Wall street)</em></strong>, lần đầu ti&ecirc;n, qu&yacute; vị độc giả được nghe Edward Thorp kể về h&agrave;nh tr&igrave;nh đi đến đỉnh cao của sự th&agrave;nh c&ocirc;ng trong sự nghiệp đầu tư của m&igrave;nh. H&agrave;nh tr&igrave;nh được bắt đầu từ những đam m&ecirc; thời ni&ecirc;n thiếu kết hợp với sự t&ograve; m&ograve; s&acirc;u sắc đ&atilde; dẫn &ocirc;ng tới c&aacute;c c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu cụ thể, những luận điểm phản biện lại c&aacute;c l&yacute; thuyết v&agrave; c&aacute;c ngộ nhận th&ocirc;ng thường trong học thuật. Cuối c&ugrave;ng sao cho hiệu quả l&agrave; việc ứng dụng những kiến thức n&agrave;y v&agrave;o thực tiến chơi b&agrave;i v&agrave; đầu tư chứng kho&aacute;n.</p>\r\n\r\n<p>Tư duy th&ocirc;ng th&aacute;i, kiến thức lỗi lạc gắn liền với thực tế của Thorp đ&atilde; được hệ thống v&agrave; tr&igrave;nh b&agrave;y lại trong s&aacute;ch c&oacute; thể gi&uacute;p cho độc giả, những nh&agrave; đầu tư tr&ecirc;n thị trường t&agrave;i ch&iacute;nh vững v&agrave;ng đi qua &ldquo;gi&ocirc;ng b&atilde;o&rdquo; v&agrave;o những thời khắc kh&oacute; khăn nhất. Đồng thời, kiến thức n&agrave;y sẽ gi&uacute;p ch&uacute;ng ta ph&aacute;t hiện ra sự gian lận tiềm ẩn, sự lọc lừa của thị trường t&agrave;i ch&iacute;nh để c&oacute; thể tr&aacute;nh xa, từ đ&oacute; bảo vệ th&agrave;nh quả v&agrave; những đồng &ldquo;tiền mồ h&ocirc;i xương m&aacute;u&rdquo; của m&igrave;nh.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i, đội ngũ bi&ecirc;n tập của Happy Live, xin tr&acirc;n trọng giới thiệu tới độc giả (những nh&agrave; đầu tư, những traders chuy&ecirc;n nghiệp, những người y&ecirc;u th&iacute;ch thử th&aacute;ch bản th&acirc;n với c&aacute;c tr&ograve; chơi rủi ro, mạo hiểm) cuốn s&aacute;ch n&agrave;y với mong muốn đ&oacute;ng g&oacute;p&nbsp;<strong>cho sự gi&agrave;u c&oacute; v&agrave; th&agrave;nh c&ocirc;ng của qu&yacute; vị.</strong>&nbsp;Hi vọng rằng hệ thống kiến thức được tổng hợp trong s&aacute;ch sẽ hữu &iacute;ch cho độc giả về sự định hướng v&agrave; th&ocirc;ng tuệ trong tư duy gắn liền với c&aacute;c trải nghiệm thực tế từ một nh&agrave; đầu tư, đầu cơ vĩ đại nhất tr&ecirc;n thế giới để l&agrave;m kim chỉ nam hướng dẫn cho c&aacute;c hoạt động thị trường h&agrave;ng ng&agrave;y của m&igrave;nh.</p>\r\n\r\n<p>Một cuốn s&aacute;ch d&ugrave; d&agrave;i nhưng sẽ kh&ocirc;ng l&atilde;ng ph&iacute; th&igrave; giờ một ch&uacute;t n&agrave;o, v&igrave; c&aacute;c bạn sẽ được hướng dẫn hoặc phải tự nghĩ v&agrave; suy nghĩ rất nhiều về những logic tr&ecirc;n một thị trường t&agrave;i ch&iacute;nh nhiều l&uacute;c rất phi l&yacute; tr&iacute;.</p>\r\n\r\n<p><strong>MỤC LỤC&nbsp;</strong></p>\r\n\r\n<p><strong>CHƯƠNG 1.</strong>&nbsp;Y&Ecirc;U TH&Iacute;CH VIỆC HỌC HỎI</p>\r\n\r\n<p><strong>CHƯƠNG 2.</strong>&nbsp;KHI KHOA HỌC L&Agrave; S&Acirc;N CHƠI CỦA T&Ocirc;I</p>\r\n\r\n<p><strong>CHƯƠNG 3.</strong>&nbsp;VẬT L&Yacute; V&Agrave; TO&Aacute;N HỌC</p>\r\n\r\n<p><strong>CHƯƠNG 4.</strong>&nbsp;LAS VEGAS</p>\r\n\r\n<p><strong>CHƯƠNG 5.</strong>&nbsp;CHINH PHỤC BLACKJACK</p>\r\n\r\n<p><strong>CHƯƠNG 6.</strong>&nbsp;NG&Agrave;Y L&Ecirc;N NG&Ocirc;I CỦA CH&Uacute; CỪU</p>\r\n\r\n<p><strong>CHƯƠNG 7.</strong>&nbsp;HỆ THỐNG ĐẾM B&Agrave;I CHO TẤT CẢ MỌI NGƯỜI</p>\r\n\r\n<p><strong>CHƯƠNG 8.</strong>&nbsp;CUỘC CHIẾN GIỮA NGƯỜI CHƠI V&Agrave; S&Ograve;NG B&Agrave;I</p>\r\n\r\n<p><strong>CHƯƠNG 9.</strong>&nbsp;M&Aacute;Y T&Iacute;NH DỰ ĐO&Aacute;N ROULETTE</p>\r\n\r\n<p><strong>CHƯƠNG 10.</strong>&nbsp;LỢI THẾ TRONG C&Aacute;C TR&Ograve; C&Aacute; CƯỢC KH&Aacute;C</p>\r\n\r\n<p><strong>CHƯƠNG 11.</strong>&nbsp;WALL STREET: S&Ograve;NG BẠC VĨ ĐẠI NHẤT THẾ GIỚI</p>\r\n\r\n<p><strong>CHƯƠNG 12.</strong>&nbsp;CHƠI B&Agrave;I BRIDGE VỚI BUFFET</p>\r\n\r\n<p><strong>CHƯƠNG 13.</strong>&nbsp;TH&Agrave;NH LẬP C&Ocirc;NG TY HỢP DANH</p>\r\n\r\n<p><strong>CHƯƠNG 14.</strong>&nbsp;CHẠY TRƯỚC TRONG CUỘC C&Aacute;CH MẠNG PH&Acirc;N T&Iacute;CH ĐỊNH LƯỢNG</p>\r\n\r\n<p><strong>CHƯƠNG 15.</strong>&nbsp;PHẤT L&Ecirc;N</p>\r\n\r\n<p><strong>CHƯƠNG 16.</strong>&nbsp;V&Agrave; RỚT XUỐNG</p>\r\n\r\n<p><strong>CHƯƠNG 17.</strong>&nbsp;GIAI ĐOẠN ĐIỀU CHỈNH</p>\r\n\r\n<p><strong>CHƯƠNG 18.</strong>&nbsp;LỪA ĐẢO V&Agrave; SỰ ĐỘC HẠI</p>\r\n\r\n<p><strong>CHƯƠNG 19.</strong>&nbsp;MUA THẤP, B&Aacute;N CAO</p>\r\n\r\n<p><strong>CHƯƠNG 20.</strong>&nbsp;THU HOẠCH TỪ HOẠT ĐỘNG NG&Acirc;N H&Agrave;NG</p>\r\n\r\n<p><strong>CHƯƠNG 21.</strong>&nbsp;R&Iacute;T MỘT HƠI CUỐI</p>\r\n\r\n<p><strong>CHƯƠNG 22.</strong>&nbsp;PH&Ograve;NG HỘ C&Aacute;C KHOẢN CƯỢC</p>\r\n\r\n<p><strong>CHƯƠNG 23.</strong>&nbsp;GI&Agrave;U BAO NHI&Ecirc;U MỚI L&Agrave; GI&Agrave;U?</p>\r\n\r\n<p><strong>CHƯƠNG 24.</strong>&nbsp;L&Atilde;I K&Eacute;P: KỲ QUAN THỨ T&Aacute;M CỦA THẾ GIỚI</p>\r\n\r\n<p><strong>CHƯƠNG 25.</strong>&nbsp;Đ&Aacute;NH BẠI HẦU HẾT C&Aacute;C NH&Agrave; ĐẦU TƯ BẰNG DANH MỤC GIẢ LẬP CHỈ SỐ</p>\r\n\r\n<p><strong>CHƯƠNG 26.</strong>&nbsp;BẠN C&Oacute; THỂ Đ&Aacute;NH BẠI THỊ TRƯỜNG ĐƯỢC KH&Ocirc;NG?</p>\r\n\r\n<p><strong>CHƯƠNG 27.</strong>&nbsp;PH&Acirc;N BỔ V&Agrave; QUẢN L&Yacute; T&Agrave;I SẢN</p>\r\n\r\n<p><strong>CHƯƠNG 28.</strong>&nbsp;CHO ĐI</p>\r\n\r\n<p><strong>CHƯƠNG 29.</strong>&nbsp;C&Aacute;C CUỘC KHỦNG HOẢNG T&Agrave;I CH&Iacute;NH: NHỮNG B&Agrave;I HỌC CHƯA THUỘC</p>\r\n\r\n<p><strong>CHƯƠNG 30.</strong>&nbsp;NHỮNG SUY TƯ</p>\r\n', '289000', 3, 1, 5, 'oknfv5rz.png', '2020-11-09', 'admin', '2020-12-10', 'admin', 1, 8, 12),
(20, 'Lột xác để trở thành nhà đầu tư giá trị', '<p><strong><em>&ldquo;Lột X&aacute;c Để Trở Th&agrave;nh Nh&agrave; Đầu Tư Gi&aacute; Trị&rdquo;</em>&nbsp;</strong>l&agrave; c&acirc;u chuyện về cuộc h&agrave;nh tr&igrave;nh của Guy Spier v&agrave; những điều &ocirc;ng đ&atilde; học được tr&ecirc;n con đường đầu tư của m&igrave;nh.</p>\r\n\r\n<p>Xuy&ecirc;n suốt qu&aacute; tr&igrave;nh đọc s&aacute;ch, độc giả v&agrave; cũng l&agrave; những nh&agrave; đầu tư hoặc y&ecirc;u th&iacute;ch c&ocirc;ng việc đầu tư sẽ thực sự trải nghiệm h&agrave;nh tr&igrave;nh lột x&aacute;c thực sự của Guy Spier từ một&nbsp;<strong>tay &ldquo;mafia&rdquo; c&ograve; mồi</strong>&nbsp;tại một quỹ đầu tư chuy&ecirc;n kinh doanh c&aacute;c cổ phiếu &ldquo;r&aacute;c&rdquo;. Tới việc trở th&agrave;nh một&nbsp;<strong>nh&agrave; đầu tư gi&aacute; trị ch&acirc;n ch&iacute;nh</strong>&nbsp;được soi s&aacute;ng bởi tr&iacute; tuệ của Warren Buffet v&agrave; Charlie Munger.</p>\r\n\r\n<p>H&agrave;nh tr&igrave;nh ấy vốn rất gian kh&oacute; với nhiều người, tuy nhi&ecirc;n th&ocirc;ng qua c&acirc;u văn v&agrave; lời chia sẻ th&agrave;nh thật của Guy Spier, qu&yacute; độc giả sẽ nhận thấy qu&aacute; tr&igrave;nh chuyển h&oacute;a v&agrave; học hỏi trở th&agrave;nh một nh&agrave; đầu tư gi&aacute; trị chưa bao giờ ch&acirc;n thực v&agrave; giản đơn đến thế!</p>\r\n\r\n<p>T&aacute;c giả Guy Spier bắt đầu sự nghiệp của m&igrave;nh với tư c&aacute;ch l&agrave; một Wannabe Gordon Gekko (nh&acirc;n vật ch&iacute;nh của bộ phim Wall Street) &ndash;&nbsp;<strong>gian manh, thiển cận, v&agrave; ho&agrave;n to&agrave;n vị kỷ.</strong>&nbsp;Một tay sinh vi&ecirc;n cao ngạo, tự phụ tốt nghiệp Oxford v&agrave; MBA Harvard danh gi&aacute; l&agrave;m việc tại một ng&acirc;n h&agrave;ng đầu tư ở phố Wall &ndash; nơi m&agrave; ma lực của đồng tiền đ&atilde; khiến cả những người kh&ocirc;n ngoan, cần mẫn nhất cũng phải nh&uacute;ng ch&agrave;m. Gần 2 năm r&ograve;ng r&atilde; tại D. H. Blair đ&atilde; l&agrave;m vấy bẩn hồ sơ đẹp đẽ của Guy Spier v&agrave; đẩy sự nghiệp của &ocirc;ng xuống bờ vực thẳm. Điều tối kị trong kinh doanh, đặc biệt l&agrave; giới đầu tư, khi danh tiếng l&agrave; tất cả&hellip;</p>\r\n\r\n<p>Guy đ&atilde; t&igrave;m thấy chiếc phao cứu sinh của đời m&igrave;nh khi kh&aacute;m ph&aacute; ra&nbsp;<strong>thế giới của Warren Buffett</strong>. Tiếp đến l&agrave; một loạt những biến đổi v&agrave; qu&aacute; tr&igrave;nh tự nhận thức dẫn dắt &ocirc;ng tr&ecirc;n con đường từ Nh&agrave; đầu tư th&ocirc;ng minh Benjamin Graham đến những triết l&yacute; đầu tư vĩ đại của Charlie Munger trong cuốn Poor Charlie&rsquo;s Almanack (Tạm dịch: Ni&ecirc;n gi&aacute;m Charlie kh&oacute; ngh&egrave;o). Tiếp đ&oacute; l&agrave; cuộc gặp với Mohnish Pabrai v&agrave; bữa ăn trưa c&ugrave;ng Warren Buffett. Bữa ăn được đ&aacute;nh đổi bằng 650.100 đ&ocirc; la ấy đ&atilde; c&oacute; một t&aacute;c động đổi đời đối với Guy Spier.<br />\r\nCũng tr&ecirc;n h&agrave;nh tr&igrave;nh n&agrave;y, &ocirc;ng gặt h&aacute;i được nhiều triết l&yacute; th&acirc;m s&acirc;u về đầu tư v&agrave; kinh doanh, đ&oacute; l&agrave;:</p>\r\n\r\n<p>&ndash; Tại sao người cố vấn v&agrave; h&igrave;nh mẫu noi theo lại đ&oacute;ng vai tr&ograve; quan trọng giống như ch&igrave;a kh&oacute;a của nh&agrave; đầu tư để đạt th&agrave;nh c&ocirc;ng trong d&agrave;i hạn<br />\r\n&ndash; Kiến thức học thuật từ nền gi&aacute;o dục h&agrave;ng đầu c&oacute; thể cản trở bạn như thế n&agrave;o<br />\r\n&ndash; Tại sao tự học lại quan trọng trong h&agrave;nh tr&igrave;nh trở th&agrave;nh nh&agrave; đầu tư vĩ đại<br />\r\n&ndash; C&aacute;ch Buffett nghiệm ra được mục ti&ecirc;u cuối c&ugrave;ng trong đời l&agrave; sống thật với ch&iacute;nh m&igrave;nh.</p>\r\n\r\n<p><strong><em>&ldquo;Lột X&aacute;c Để Trở Th&agrave;nh Một Nh&agrave; Đầu Tư Gi&aacute; Trị&rdquo;</em></strong>&nbsp;của Guy Spier thực sự l&agrave; một cuốn s&aacute;ch gi&aacute; trị tới từng chữ v&agrave; ở từng &yacute; tưởng. Một cuốn hồi k&yacute; cực kỳ thẳng thắn đưa người đọc v&agrave;o c&aacute;c g&oacute;c tối nhất của Phố Wall. Đồng thời cũng l&agrave; một bản hướng dẫn thực tế v&agrave; th&ocirc;ng minh gi&uacute;p bạn trở th&agrave;nh một nh&agrave; đầu tư th&agrave;nh c&ocirc;ng. Những b&agrave;i học m&agrave; bạn chỉ cần học được một v&agrave;i trong số n&agrave;y th&ocirc;i, bạn sẽ kh&ocirc;ng c&ograve;n sự lựa chọn n&agrave;o kh&aacute;c ngo&agrave;i việc trở n&ecirc;n gi&agrave;u c&oacute; &ndash; v&agrave; c&oacute; thể l&agrave; cực kỳ gi&agrave;u c&oacute;.</p>\r\n\r\n<p>S&aacute;ch dẫn dắt những người mới tham gia đầu tư chứng kho&aacute;n; hoặc những nh&agrave; đầu tư đang lạc lối v&agrave; thua lỗ c&oacute; thể t&igrave;m thấy cho m&igrave;nh một con đường s&aacute;ng. Hay đơn giản, cuốn s&aacute;ch n&agrave;y l&agrave; để d&agrave;nh cho những ai vốn đ&atilde; biết đến v&agrave; y&ecirc;u th&iacute;ch phương ph&aacute;p đầu tư gi&aacute; trị, gi&uacute;p họ củng cố th&ecirc;m niềm tin v&agrave; t&igrave;m th&ecirc;m đồng minh về l&yacute; tưởng tr&ecirc;n h&agrave;nh tr&igrave;nh đi t&igrave;m ch&acirc;n l&yacute; trong đầu tư.</p>\r\n\r\n<h3><strong>S&aacute;ch được đ&aacute;nh gi&aacute; 4,5/5 sao tại amazon với 383 nhận x&eacute;t.</strong></h3>\r\n\r\n<p>VỀ T&Aacute;C GIẢ</p>\r\n\r\n<p>Guy Spier l&agrave; một nh&agrave; quản l&yacute; qũy ph&ograve;ng hộ nổi danh, hiện &ocirc;ng đang điều h&agrave;nh quỹ Aquamarine Fund với lợi nhuận 463% từ khi th&agrave;nh lập v&agrave;o năm 1997. &Ocirc;ng cũng l&agrave; một nh&agrave; ph&acirc;n t&iacute;ch thường xuy&ecirc;n xuất hiện tr&ecirc;n c&aacute;c k&ecirc;nh phương tiện truyền th&ocirc;ng như CNN, CNBO, Bloomberg&hellip;</p>\r\n\r\n<p>Guy nổi tiếng với việc c&ugrave;ng NĐT Mohnish Pabrai chi $650,000 (tương đương hơn 13 tỷ đồng) trong một buổi đấu gi&aacute; từ thiện để được ăn trưa với Buffett.</p>\r\n\r\n<h2>NHẬN X&Eacute;T VỀ S&Aacute;CH</h2>\r\n\r\n<p>Cực kỳ hiếm để t&igrave;m ra một cuốn s&aacute;ch c&oacute; thể đọc được về đầu tư gi&aacute; trị, v&agrave; đ&acirc;y thực sự l&agrave; một cuốn s&aacute;ch hiếm c&oacute;. Khi bạn cầm cuốn s&aacute;ch n&agrave;y l&ecirc;n, t&ocirc;i c&aacute; l&agrave; bạn sẽ kh&ocirc;ng thể bỏ n&oacute; xuống được. V&agrave; nhiều khả năng bạn sẽ trở th&agrave;nh một con người tốt hơn sau khi đọc xong cuốn s&aacute;ch n&agrave;y.<br />\r\n-Len Schlesinger, Baker Foundation Professor, Harvard Business school &ndash;</p>\r\n\r\n<p>&ldquo;Lột x&aacute;c để trở th&agrave;nh một nh&agrave; đầu tư gi&aacute; trị&rdquo; l&agrave; một h&agrave;nh tr&igrave;nh rất trung thực, mới mẻ v&agrave; đầy t&iacute;nh khi&ecirc;m tốn của Guy Spier trong qu&aacute; tr&igrave;nh trở th&agrave;nh một nh&agrave; đầu tư gi&aacute; trị. Những b&agrave;i học của Guy chứa đựng trong những trang giấy mang đến cho bạn những gi&aacute; trị lớn lao m&agrave; b&aacute;c của t&ocirc;i, Sir John Templeton, sẽ cực kỳ tr&acirc;n trọng v&agrave; y&ecirc;u th&iacute;ch trong triết l&yacute; đầu tư của &ocirc;ng ấy. Tại sao bạn lại phải học từ những lỗi lầm của ch&iacute;nh m&igrave;nh (điều cực k&igrave; đắt đỏ trong thị trường) trong khi bạn c&oacute; thể học được ch&uacute;ng ngay từ những lời khuy&ecirc;n đắt gi&aacute; của một chuy&ecirc;n gia đầu tư như Guy Spier?&rdquo;<br />\r\n&ndash; Lauren c. Templeton, s&aacute;ng lập v&agrave; Chủ tịch của Lauren Templeton Capital Management &ndash;</p>\r\n\r\n<p>&ldquo;Nh&agrave; đầu tư gi&aacute; trị nổi tiếng Guy Spier đ&atilde; l&agrave;m c&aacute;ch n&agrave;o đ&oacute; để c&oacute; thể viết ra những k&iacute; ức rất th&uacute; vị v&agrave; những h&agrave;nh tr&igrave;nh đầy say m&ecirc; về những điều cần thiết để th&agrave;nh c&ocirc;ng trong đầu tư v&agrave; trong cuộc sống. Một cuốn s&aacute;ch PHẢI ĐỌC&rdquo;<br />\r\n&ndash; John Mihaljevic, CFA, Mananging Editor của The Manual of ideas &ndash;</p>\r\n\r\n<p>&ldquo;Ng&agrave;nh c&ocirc;ng nghiệp t&agrave;i ch&iacute;nh cực kỳ cạnh tranh v&agrave; việc biết một ai đ&oacute; rất r&otilde; l&agrave; một lợi thế cạnh tranh lớn. Trong cuốn s&aacute;ch tuyệt vời n&agrave;y, Guy đ&atilde; gi&uacute;p ch&uacute;ng ta hiểu h&agrave;nh tr&igrave;nh m&agrave; anh ta đ&atilde; kh&aacute;m ph&aacute; ra c&aacute;ch thức của ch&iacute;nh anh ấy khi l&agrave;m chuyện đ&oacute;. C&aacute;ch kể chuyện của Guy đ&atilde; tặng cho ch&uacute;ng ta những m&oacute;n qu&agrave; gi&uacute;p ch&uacute;ng ta hiểu bản th&acirc;n m&igrave;nh tốt hơn, nhờ đ&oacute; trở th&agrave;nh những nh&agrave; đầu tư giỏi hơn. T&ocirc;i rất ấn tượng v&agrave; được truyền cảm hứng bởi anh ấy&rdquo;.<br />\r\n&ndash; Lisa O&rsquo;dell Rapuano, S&aacute;ng lập của Lane Five Capital Management &ndash;</p>\r\n', '269000', 3, 0, 0, 'jl7gwduo.png', '2020-11-09', 'admin', '2020-12-10', 'admin', 1, 6, 12),
(21, 'Hướng dẫn giao dịch theo sóng Elliott', '<h2>&nbsp;</h2>\r\n\r\n<p><strong>Wayne Gorman</strong>, từng l&agrave; nh&agrave; giao dịch tại Citibank v&agrave; Westpac Banking Corporation, nay l&agrave; Trưởng Bộ Phận T&agrave;i Nguy&ecirc;n Gi&aacute;o Dục của EWI.&nbsp;<strong>Jeffrey Kennedy</strong>&nbsp;l&agrave; người đang tự kiếm sống bằng nghề giao dịch t&agrave;i ch&iacute;nh v&agrave; cũng l&agrave; nh&agrave; bi&ecirc;n tập của b&aacute;o c&aacute;o Elliott Wave Junctures (một dịch vụ gi&aacute;o dục h&agrave;ng ng&agrave;y v&agrave; nhận diện c&aacute;c cơ hội giao dịch ngắn hạn). Cả hai qu&yacute; &ocirc;ng n&agrave;y đang l&agrave; giảng vi&ecirc;n trong c&aacute;c buổi hướng dẫn giao dịch s&oacute;ng Elliott của EWI. Cả hai đều l&agrave; những t&aacute;c giả danh tiếng.</p>\r\n\r\n<p>Wayne v&agrave; Jeff đ&atilde; thể hiện r&otilde; r&agrave;ng, từng bước một, c&aacute;ch tư duy của họ trong một số giao dịch thực tế. Họ cũng tr&igrave;nh b&agrave;y c&aacute;c hướng dẫn thiết lập t&iacute;n hiệu giao dịch l&yacute; thuyết nhằm gi&uacute;p bạn c&oacute; được kinh nghiệm khi giao dịch tr&ecirc;n thị trường của m&igrave;nh. Một điều rất đ&aacute;ng tr&acirc;n trọng v&agrave; kh&aacute;c biệt với nhiều chuy&ecirc;n gia kh&aacute;c (thường che giấu sai lầm của họ) l&agrave; Wayne v&agrave; Jeff kh&ocirc;ng ngần ngại chia sẻ những sai lầm của họ, cũng như c&aacute;c b&agrave;i học kinh nghiệm được r&uacute;t ra. Khi bạn đọc những thảo luận của hai t&aacute;c giả n&agrave;y, bạn sẽ nhận ra họ đ&atilde; trải qua con đường gian nan như thế n&agrave;o để đ&uacute;c r&uacute;t c&aacute;c kinh nghiệm giao dịch. Qua những v&iacute; dụ thực tế, bạn sẽ nhận ra l&agrave;m thế n&agrave;o để c&oacute; được những ph&acirc;n t&iacute;ch v&agrave; giao dịch hợp l&yacute;.</p>\r\n\r\n<p>Thẳng thắn m&agrave; n&oacute;i, kh&ocirc;ng ai sinh ra đ&atilde; c&oacute; năng khiếu thi&ecirc;n bẩm để trở n&ecirc;n giao dịch th&agrave;nh c&ocirc;ng. Ch&uacute;ng ta đều sẵn c&oacute; sự bốc đồng, t&iacute;nh nh&uacute;t nh&aacute;t, lười biếng hoặc những c&aacute; t&iacute;nh xấu. Cuốn s&aacute;ch n&agrave;y sẽ cho bạn thấy hai t&aacute;c giả đ&atilde; r&egrave;n luyện v&agrave; sửa chữa c&aacute;c khuyết điểm t&acirc;m l&yacute; như thế n&agrave;o khi giao dịch tr&ecirc;n thị trường t&agrave;i ch&iacute;nh.</p>\r\n\r\n<p><strong>Nếu bạn muốn giao dịch kiếm sống bằng ch&iacute;nh đồng vốn của m&igrave;nh, nếu bạn muốn trở th&agrave;nh một nh&agrave; giao dịch th&agrave;nh c&ocirc;ng, nếu bạn muốn chỉ giao dịch với những t&iacute;n hiệu tốt nhất, hoặc thậm ch&iacute;, rất đơn giản, nếu mục ti&ecirc;u của bạn chỉ l&agrave; &ldquo;kh&ocirc;ng để mất tiền&rdquo; khi tham gia v&agrave;o thị trường t&agrave;i ch&iacute;nh, th&igrave; đ&acirc;y ch&iacute;nh l&agrave; cuốn s&aacute;ch bạn cần.</strong></p>\r\n\r\n<p>Giao dịch th&agrave;nh c&ocirc;ng đ&ograve;i hỏi phải l&agrave;m việc nghi&ecirc;m t&uacute;c, t&iacute;nh kỷ luật v&agrave; cả sự nhanh nhạy. Nhưng ngay cả khi c&oacute; được ba điều tr&ecirc;n, bạn cũng chỉ mới tiến gần đến th&agrave;nh c&ocirc;ng m&agrave; th&ocirc;i. Điều cuối c&ugrave;ng gi&uacute;p bạn c&oacute; được th&agrave;nh c&ocirc;ng bền vững ch&iacute;nh l&agrave; tri thức. V&agrave; đ&acirc;y l&agrave; điều cuốn s&aacute;ch n&agrave;y mang lại.</p>\r\n\r\n<p><em><strong>Robert R. Prechter, Jr.</strong></em></p>\r\n\r\n<p><em><strong>Nh&agrave; s&aacute;ng lập Elliott Wave International (EWI)</strong></em></p>\r\n\r\n<h2>Lời giới thiệu của nh&oacute;m t&aacute;c giả</h2>\r\n\r\n<p>Ch&agrave;o mừng c&aacute;c bạn! Hướng Dẫn Giao Dịch Theo S&oacute;ng Elliott l&agrave; cuốn s&aacute;ch phải đọc d&agrave;nh cho những ai muốn biết c&aacute;ch thức giao dịch theo Nguy&ecirc;n L&yacute; S&oacute;ng Elliott. Bạn sẽ biết l&agrave;m thế n&agrave;o để t&igrave;m ra c&aacute;c cơ hội giao dịch tốt, đ&aacute;nh gi&aacute; chất lượng từng t&iacute;n hiệu giao dịch, c&aacute;ch mở vị thế, quản trị giao dịch bằng c&aacute;ch n&acirc;ng v&agrave; hạ lệnh dừng lỗ, cũng như c&aacute;ch đ&oacute;ng lệnh hợp l&yacute; nhất.</p>\r\n\r\n<p>Cuốn s&aacute;ch Hướng Dẫn Giao Dịch Theo S&oacute;ng Elliott giả định bạn đọc l&agrave; những người đ&atilde; quen thuộc với Nguy&ecirc;n L&yacute; S&oacute;ng Elliott v&agrave; c&aacute;ch thức ứng dụng l&yacute; thuyết n&agrave;y. Giống như nhiều cuốn s&aacute;ch dạy chiến thuật đ&aacute;nh cờ tướng (lu&ocirc;n giả định bạn phải c&oacute; những kiến thức cơ bản về c&aacute;ch chơi cờ), cuốn s&aacute;ch n&agrave;y cũng giả định bạn đọc đ&atilde; nắm sơ lược c&aacute;c kiến thức cơ bản về một số mẫu h&igrave;nh S&oacute;ng Elliott v&agrave; l&agrave;m thế n&agrave;o để kết hợp ch&uacute;ng lại với nhau.</p>\r\n\r\n<p><strong>Nếu bạn l&agrave; một nh&agrave; ph&acirc;n t&iacute;ch v&agrave; giao dịch s&oacute;ng Elliott đầy kinh nghiệm</strong>&nbsp;v&agrave; cần một bản t&oacute;m lược đơn giản, đầy đủ, x&uacute;c t&iacute;ch về Nguy&ecirc;n L&yacute; S&oacute;ng, th&igrave; Phụ Lục Nguy&ecirc;n L&yacute; S&oacute;ng Elliott đ&aacute;p ứng tất cả c&aacute;c y&ecirc;u c&acirc;u n&agrave;y.</p>\r\n\r\n<p><strong>Nếu bạn l&agrave; một người chưa hề biết g&igrave; về Nguy&ecirc;n L&yacute; S&oacute;ng Elliott hoặc bạn muốn nghi&ecirc;n cứu chuy&ecirc;n s&acirc;u,</strong>&nbsp;ch&uacute;ng t&ocirc;i đề nghị bạn đọc n&ecirc;n tham khảo c&aacute;c t&agrave;i liệu đ&iacute;nh k&egrave;m trong cuốn s&aacute;ch n&agrave;y v&agrave; truy cập tới phi&ecirc;n bản miễn ph&iacute; cuốn s&aacute;ch kinh điển tr&ecirc;n phố Wall: &ldquo;Elliott Wave Principle (Nguy&ecirc;n L&yacute; S&oacute;ng Elliott)&rdquo; của Frost v&agrave; Prechter theo đường link sau: www.elliottwave.com/wave/ReaderResources (Lưu &yacute;, nh&oacute;m dịch kh&ocirc;ng chuyển dịch sang tiếng việt v&igrave; kh&ocirc;ng c&oacute; bản quyền).</p>\r\n\r\n<p>Cả hai ch&uacute;ng t&ocirc;i đ&atilde; v&agrave; đang giao dịch kiếm sống bằng kỷ luật tu&acirc;n thủ Nguy&ecirc;n L&yacute; S&oacute;ng Elliott. Hướng Dẫn Giao Dịch Theo S&oacute;ng Elliott l&agrave; một h&agrave;nh tr&igrave;nh trải nghiệm của ch&uacute;ng t&ocirc;i khi &aacute;p dụng Nguy&ecirc;n L&yacute; S&oacute;ng Elliott v&agrave;o thực tiễn: Ch&uacute;ng t&ocirc;i nh&igrave;n thấy điều g&igrave;, ch&uacute;ng t&ocirc;i lờ đi điều g&igrave;, ch&uacute;ng t&ocirc;i đ&atilde; l&agrave;m đ&uacute;ng c&aacute;i g&igrave;, v&agrave; ch&uacute;ng t&ocirc;i đ&atilde; sai lầm như thế n&agrave;o.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i kh&ocirc;ng muốn tr&igrave;nh b&agrave;y những v&iacute; dụ ho&agrave;n hảo khiến bạn ngộ nhận rằng, bạn c&oacute; thể giao dịch l&agrave;m gi&agrave;u chỉ trong v&ograve;ng 30 ph&uacute;t v&agrave; d&agrave;nh phần lớn thời gian trong ng&agrave;y để chơi golf. Thay v&agrave;o đ&oacute;, ch&uacute;ng t&ocirc;i cố gắng tạo ra một cuốn s&aacute;ch hướng dẫn giao dịch giống với thực tế nhất. V&igrave; thế, ch&uacute;ng t&ocirc;i sẽ ph&ocirc; b&agrave;y tất cả những sai lầm, những điều kh&ocirc;ng ho&agrave;n hảo cho từng giao dịch, để cho bạn đọc hiểu rằng, c&oacute; nhiều c&aacute;ch giao dịch th&agrave;nh c&ocirc;ng khi tu&acirc;n thủ kỷ luật t&iacute;n hiệu giao dịch theo s&oacute;ng Elliott.</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i hy vọng bạn đọc sẽ y&ecirc;u th&iacute;ch cuốn s&aacute;ch Hướng Dẫn Giao Dịch Theo S&oacute;ng Elliott. N&agrave;o ch&uacute;ng ta h&atilde;y bắt đầu h&agrave;nh tr&igrave;nh.</p>\r\n\r\n<p><em><strong>Wayne Gorman v&agrave; Jeffrey Kennedy</strong></em></p>\r\n', '251000', 5, 0, 5, 'cr9uodbt.png', '2020-11-09', 'admin', '2020-12-10', 'admin', 1, 11, 12),
(22, 'Nghệ thuật đầu tư Dhandho', '<p>Khi nhắc tới đầu tư gi&aacute; trị, ch&uacute;ng ta kh&ocirc;ng thể kh&ocirc;ng nhắc đến người đ&agrave;n &ocirc;ng lỗi lạc, người được v&iacute; như một &ldquo;Warren Buffett đời thứ hai&rdquo;, đ&oacute; l&agrave; Mohnish Pabrai.</p>\r\n\r\n<p>Thuộc diện sinh sau đẻ muộn lại kh&ocirc;ng được đ&agrave;o tạo b&agrave;i bản về t&agrave;i ch&iacute;nh nhưng nhưng Mohnish Pabrai đ&atilde; trở th&agrave;nh một nh&agrave; đầu tư v&ocirc; c&ugrave;ng th&agrave;nh c&ocirc;ng. &Ocirc;ng được xếp v&agrave;o h&agrave;ng ngũ những nh&agrave; đầu tư gi&aacute; trị nổi danh thế hệ mới c&ugrave;ng Phil Town, Guy Spier, Seth Klarman; những con người kế thừa một c&aacute;ch xuất sắc phương ph&aacute;p đầu tư của c&aacute;c bậc &ldquo;tiền bối&rdquo; Benjamin Graham, Warren Buffett v&agrave; Charlie Munger. Mohnish được biết tới bởi c&aacute;ch tiếp cận cực k&igrave; th&agrave;nh c&ocirc;ng trong d&agrave;i hạn với th&agrave;nh t&iacute;ch đ&aacute;ng nể: đạt l&atilde;i k&eacute;p tới 18.8%/năm suốt giai đoạn kh&oacute; khăn của thị trường chứng kho&aacute;n Hoa Kỳ từ 1999-2008 (thị trường chung chỉ mang lại mức lợi nhuận khoảng 1%) v&agrave; biến 1 triệu đ&ocirc; la ban đầu của trong quỹ đầu tư của m&igrave;nh (năm 1999) tăng trưởng th&agrave;nh 400 triệu đ&ocirc; la ng&agrave;y nay.</p>\r\n\r\n<p>NĐT Mohnish Pabrai &ndash; t&aacute;c giả s&aacute;ch Nghệ thuật đầu tư Dhandho</p>\r\n\r\n<p>Lối tiếp cận truyền thống của hầu hết c&aacute;c nh&agrave; đầu tư tr&ecirc;n thực tế lu&ocirc;n l&agrave; &ldquo;Rủi ro cao, lợi nhuận cao&rdquo; nhưng với Mohnish Pabrai th&igrave; kh&ocirc;ng nhất thiết phải như vậy.&nbsp;<strong>&ldquo;Rủi ro thấp, lợi nhuận cao&rdquo;</strong>&nbsp;đ&oacute; l&agrave; tất cả những g&igrave; &ocirc;ng t&igrave;m kiếm.</p>\r\n\r\n<p>Mohnish Pabrai th&ocirc;ng qua cuốn s&aacute;ch nổi tiếng của m&igrave;nh &ndash;&nbsp;<strong>&ldquo;Nghệ Thuật Đầu Tư Dhandho &ndash; The Dhandho Investor&rdquo;</strong>&nbsp;sẽ từng bước chỉ ra cho ch&uacute;ng ta những đặc điểm v&agrave; c&aacute;ch thức đầu tư m&agrave; &ocirc;ng đ&atilde; &aacute;p dụng rất hiệu quả trong thực tế. V&agrave; thật l&agrave; may mắn những &ldquo;t&uacute;i kh&ocirc;n&rdquo; v&agrave; tr&iacute; tuệ của Mohnish sẽ đến v&agrave; đi thẳng v&agrave;o đầu ch&uacute;ng ta bằng lối viết v&agrave; kể chuyện đơn giản, dễ hiểu.</p>\r\n\r\n<p><strong>&ldquo;Ngửa th&igrave; t&ocirc;i thắng, sấp th&igrave; t&ocirc;i chẳng thiệt bao nhi&ecirc;u&rdquo;</strong>&nbsp;đ&oacute; ch&iacute;nh l&agrave; nguy&ecirc;n tắc lặp đi lặp lại trong cuốn s&aacute;ch th&ocirc;ng qua h&agrave;ng loạt c&aacute;c th&iacute; dụ về những ng&agrave;nh nghề, những doanh nh&acirc;n, v&agrave; những cơ hội kinh doanh kh&aacute;c nhau. Bạn sẽ hiểu được nguy&ecirc;n nh&acirc;n v&igrave; sao những người Patel, một d&acirc;n tộc thiểu số Ấn Độ di cư sang Mĩ năm 1970, với số d&acirc;n v&ocirc; c&ugrave;ng &iacute;t ỏi, lại c&oacute; thể vươn l&ecirc;n chiếm tới 50% thị phần ng&agrave;nh nghề kinh doanh nh&agrave; nghỉ (Motel) tại Mỹ, v&igrave; sao doanh nh&acirc;n Richard Branson lại đạt được th&agrave;nh c&ocirc;ng lớn với tập đo&agrave;n Virgin, v&igrave; sao &ldquo;Vua&rdquo; th&eacute;p Ấn Độ Lakshmi Mittal lại l&agrave; &ocirc;ng ho&agrave;ng trong lĩnh vực của m&igrave;nh.</p>\r\n\r\n<p>H&atilde;y tập trung nghi&ecirc;n cứu v&agrave; đầu tư v&agrave;o những c&ocirc;ng ty b&aacute;n những sản phẩm m&agrave; tất cả mọi người cần; h&atilde;y đầu tư v&agrave;o những c&ocirc;ng ty c&oacute; x&aacute;c suất thay đổi v&agrave; buộc phải thay đổi &iacute;t nhất, những c&ocirc;ng ty c&oacute; rủi ro thấp v&agrave; t&iacute;nh bất định cao; h&atilde;y biết chờ đợi v&agrave; đặt cược lớn khi lợi thế nghi&ecirc;ng về ph&iacute;a m&igrave;nh; h&atilde;y cược &iacute;t, cược lớn v&agrave; cược kh&ocirc;ng thường xuy&ecirc;n; h&atilde;y mua khi những người kh&aacute;c tuyệt vọng. Đ&oacute; l&agrave; tất cả những g&igrave; bạn cần phải l&agrave;m khi đầu tư theo phong c&aacute;ch của Mohnish Pabrai.</p>\r\n\r\n<h3><strong>S&aacute;ch được đ&aacute;nh gi&aacute; 4,5/5 sao tại amazon với 230 nhận x&eacute;t.</strong></h3>\r\n\r\n<p><strong>NHẬN X&Eacute;T VỀ S&Aacute;CH</strong></p>\r\n\r\n<p>Monish Pabrai l&agrave; một người c&oacute; tư tưởng sắc sảo, &ocirc;ng th&iacute;ch giải m&atilde; thế giới t&agrave;i ch&iacute;nh huyền b&iacute; đồng thời cũng l&agrave; người x&acirc;u chuỗi c&acirc;u chuyện đến t&agrave;i t&igrave;nh. Cho d&ugrave; bạn hoang mang thế n&agrave;o trước những thế lực đứng sau sự bấp b&ecirc;nh của gi&aacute; cổ phiếu tr&ecirc;n Phố Wall hay bạn biết chắc m&igrave;nh đ&atilde; tỏ đi nữa, bạn cũng sẽ hiểu thấu đ&aacute;o hơn khi đọc cuốn s&aacute;ch n&agrave;y. Monish Pabrai dẫn ra c&acirc;u chuyện của những &ocirc;ng tr&ugrave;m kinh doanh v&agrave; b&agrave;i thẩm tra th&ocirc;ng minh nhằm chắt lọc c&aacute;c nguy&ecirc;n tắc cốt l&otilde;i mang gi&aacute; trị s&acirc;u sắc. Tất cả được đ&oacute;ng g&oacute;i th&agrave;nh bộ &yacute; tưởng hữu &iacute;ch m&agrave; bạn n&ecirc;n vận dụng v&agrave;o ch&iacute;nh qu&aacute; tr&igrave;nh đầu tư của m&igrave;nh.</p>\r\n\r\n<p><em>&ndash; Srephane Fitch, Gi&aacute;m đốc Forbes ch&acirc;u &Acirc;u</em></p>\r\n\r\n<p>T&ocirc;i đọc Nghệ thuật đầu tư Dhandho liền một mạch v&igrave; kh&ocirc;ng k&igrave;m l&ograve;ng nổi. Mohnish chia sẻ những &ldquo;b&iacute; mật&rdquo; tạo n&ecirc;n th&agrave;nh c&ocirc;ng bứt ph&aacute; của m&igrave;nh v&agrave; ghi c&ocirc;ng v&agrave;o kho t&agrave;ng đầu tư gi&aacute; trị.</p>\r\n\r\n<p><em>&ndash; Whitney Tilson, quản l&yacute; điều h&agrave;nh kinh doanh v&agrave; s&aacute;ng lập quỹ T2 Partners LLC, nh&agrave; s&aacute;ng lập Hội nghị đầu tư gi&aacute; trị (Value Investing Congress)</em></p>\r\n\r\n<p>Nghệ thuật đầu tư Dhandho đ&atilde; ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ đơn giản h&oacute;a chiến lược đầu tư th&agrave;nh c&ocirc;ng. &ldquo;Ngửa th&igrave; t&ocirc;i thắng, sấp th&igrave; t&ocirc;i chẳng thiệt bao nhi&ecirc;u!&rdquo; T&ocirc;i kh&ocirc;ng phải mua to&agrave;n bộ doanh nghiệp, m&agrave; chỉ cần mua cổ phiếu của một v&agrave;i doanh nghiệp đang ni&ecirc;m yết. T&ocirc;i muốn giới thiệu cuốn s&aacute;ch n&agrave;y cho bất cứ ai muốn trau dồi kỹ năng đầu tư của m&igrave;nh.</p>\r\n\r\n<p><em>&ndash; Pattrick Fitzgerald, Chủ tịch của quỹ Fitzgerald Management</em></p>\r\n', '189000', 2, 0, 3, '9z6omwyh.png', '2020-11-10', 'admin', '2020-12-10', 'admin', 1, 7, 12),
(23, 'Pay Back Time - Ngày đòi nợ', '<p>Trong chứng kho&aacute;n v&agrave; thị trường t&agrave;i ch&iacute;nh, d&aacute;m tham gia đầu tư cũng l&agrave; một th&agrave;nh c&ocirc;ng t&acirc;m l&yacute; ban đầu. D&ugrave; vậy, 95% nh&agrave; đầu tư Việt Nam thuộc nh&oacute;m nh&agrave; đầu tư nhỏ lẻ với vốn kiến thức t&agrave;i ch&iacute;nh v&ocirc; c&ugrave;ng hạn chế. Họ tham gia v&agrave;o thị trường chứng kho&aacute;n với 100% &yacute; ch&iacute; v&agrave; sự quyết t&acirc;m chiến thắng thị trường, nhưng thật kh&ocirc;ng may mắn kết cục cuối c&ugrave;ng của họ lu&ocirc;n l&agrave; sự thất bại v&agrave; mất tiền.</p>\r\n\r\n<p>Bởi v&igrave;, ch&uacute;ng ta thường dễ d&agrave;ng nghe theo c&aacute;c &ldquo;lời khuy&ecirc;n miễn ph&iacute;&rdquo; đến từ bạn b&egrave;, những người m&ocirc;i giới, những người nghe ng&oacute;ng th&ocirc;ng tin từ c&aacute;c &ldquo;đội l&aacute;i&rdquo; l&agrave;m gi&aacute; v&agrave; thao t&uacute;ng chứng kho&aacute;n. Ch&uacute;ng ta kh&ocirc;ng biết l&agrave;m thế n&agrave;o để ph&acirc;n biệt hai kh&aacute;i niệm &ldquo;gi&aacute; cả &ndash; pricing&rdquo; v&agrave; &ldquo;gi&aacute; trị &ndash; value&rdquo;. Hay mua mua, b&aacute;n b&aacute;n li&ecirc;n tục h&agrave;ng ng&agrave;y với việc d&aacute;n mắt v&agrave;o bảng điện tử, ph&acirc;n t&iacute;ch biểu đồ, đường trung b&igrave;nh gi&aacute;&hellip; v&agrave; mong đợi một vận may cổ phiếu c&ugrave;ng gi&aacute; của n&oacute; sẽ đi theo suy đo&aacute;n của m&igrave;nh?</p>\r\n\r\n<p>Rất nhiều &ldquo;nh&agrave; đầu tư&rdquo; kh&ocirc;ng hề c&oacute; một hệ thống đầu tư chuẩn mực n&agrave;o cả. Kh&ocirc;ng biết c&aacute;ch v&agrave; những ti&ecirc;u ch&iacute; để t&igrave;m kiếm cổ phiếu, theo d&otilde;i, quyết định điểm mua v&agrave; quyết định thời điểm b&aacute;n ra. V&igrave; kh&ocirc;ng c&oacute; hệ thống đầu tư chuẩn mực n&ecirc;n kết quả ch&uacute;ng ta chỉ c&oacute; một sự lựa chọn đ&oacute; ch&iacute;nh l&agrave; THUA LỖ.</p>\r\n\r\n<p>Vậy c&oacute; phải chỉ c&oacute; những chuy&ecirc;n gia t&agrave;i ch&iacute;nh mới c&oacute; thể gặt h&aacute;i th&agrave;nh c&ocirc;ng tr&ecirc;n thị trường chứng kho&aacute;n? Để kiếm được tiền từ chứng kho&aacute;n bạn phải sở hữu h&agrave;ng t&aacute; kho t&agrave;ng kiến thức t&agrave;i ch&iacute;nh vốn kh&ocirc;ng dễ d&agrave;ng &ldquo;hấp thụ&rdquo; trong một sớm một chiều?</p>\r\n\r\n<p>Kh&ocirc;ng hẳn vậy. C&oacute; rất nhiều người d&ugrave; kh&ocirc;ng được đ&agrave;o tạo b&agrave;i bản về c&aacute;c kiến thức t&agrave;i ch&iacute;nh, d&ugrave; vẫn l&agrave; những người đầu tư nhỏ lẻ họ vẫn chiến thắng thị trường v&agrave; l&agrave;m gi&agrave;u được từ chứng kho&aacute;n. Đ&oacute; l&agrave; bởi v&igrave; họ c&oacute; được một hệ thống đầu tư chuẩn mực, biết vận dụng c&aacute;c c&ocirc;ng cụ ph&acirc;n t&iacute;ch, biết tu&acirc;n thủ c&aacute;c nguy&ecirc;n tắc trong đầu tư.&nbsp;</p>\r\n\r\n<p>C&oacute; một hệ thống đầu tư chuẩn mực đ&atilde; v&agrave; đang được những nh&agrave; đầu tư bậc thầy tr&ecirc;n thế giới như Warren Buffett, Charlie Munger&hellip; &aacute;p dụng. Hệ thống đầu tư v&agrave; phương ph&aacute;p đầu tư ưu việt đ&oacute; đ&atilde; được g&oacute;i gọn v&agrave; thực sự dễ hiểu trong cuốn PayBack Time &ndash; Ng&agrave;y đ&ograve;i nợ của t&aacute;c giả Phil Town.</p>\r\n\r\n<p><strong>Payback Time &ndash; Ng&agrave;y đ&ograve;i nợ</strong>&nbsp;l&agrave; cuốn s&aacute;ch b&aacute;n chạy nhất New York Time được t&aacute;c giả Phil Town sử dụng những ng&ocirc;n ngữ đơn giản, dễ hiểu v&agrave; lồng gh&eacute;p những v&iacute; dụ thực tế gi&uacute;p cho người đọc tiếp cận với những kiến thức về đầu tư chứng kho&aacute;n một c&aacute;ch dễ d&agrave;ng. B&ecirc;n cạnh đ&oacute; với những kiến thức v&agrave; trải nghiệm của bản th&acirc;n, ch&uacute;ng t&ocirc;i đ&atilde; đưa cuốn s&aacute;ch gần gũi hơn với bạn đọc Việt Nam.</p>\r\n\r\n<p>Cuốn s&aacute;ch sẽ hướng dẫn bạn từ c&aacute;ch thức lựa chọn, đ&aacute;nh gi&aacute; cổ phiếu, cho đến x&acirc;y dựng cho m&igrave;nh một danh mục c&aacute;c cổ phiếu sẽ mua, mức gi&aacute; mua&ndash;b&aacute;n n&agrave;o l&agrave; hợp l&yacute;, c&ugrave;ng với những nguy&ecirc;n tắc m&agrave; bạn phải tu&acirc;n theo&hellip; v&agrave; cứ thực h&agrave;nh như vậy cho tới khi bạn trở n&ecirc;n gi&agrave;u c&oacute;.</p>\r\n\r\n<p><em>&ldquo;Một cuộc sống hạnh ph&uacute;c được bắt đầu từ những quyết định đầu tư kh&ocirc;n ngoan&rdquo;</em>. Ngay ng&agrave;y h&ocirc;m nay, h&atilde;y bắt đầu quyết định đầu tư kh&ocirc;n ngoan của bạn bằng việc trang bị một hệ thống v&agrave; phương ph&aacute;p đầu tư ho&agrave;n chỉnh đ&atilde; được chứng minh hiệu quả tuyệt đối qua thời gian được tr&igrave;nh b&agrave;y trong cuốn s&aacute;ch n&agrave;y. Bởi v&igrave;: Kiếm một số tiền lớn từ đầu tư chứng kho&aacute;n ch&iacute;nh l&agrave; c&aacute;ch &ldquo;trả th&ugrave;&rdquo; tốt nhất cho tương lai t&agrave;i ch&iacute;nh của bạn vốn đ&atilde; bị cướp đi trước đ&acirc;y.</p>\r\n\r\n<p><strong>MỤC LỤC S&Aacute;CH</strong></p>\r\n\r\n<p><strong>Lưu &yacute; c&ugrave;ng độc giả</strong></p>\r\n\r\n<p><strong>Lời tựa</strong></p>\r\n\r\n<p><strong>Chương 1:</strong>&nbsp;Con đường từ Quy tắc số 1 &ndash;&nbsp;RULE #1 &ndash; đến Payback Time</p>\r\n\r\n<p><strong>Chương 2:</strong>&nbsp;Người gi&agrave;u đ&atilde; d&ugrave;ng&nbsp;khủng hoảng để &ldquo;PHẤT L&Ecirc;N&rdquo;&nbsp;như thế n&agrave;o?</p>\r\n\r\n<p><strong>Chương 3:&nbsp;</strong>ĐẦU TƯ v&agrave;o c&aacute;c quỹ tương hỗ&nbsp;kh&ocirc;ng mang lại hiệu quả</p>\r\n\r\n<p><strong>Chương 4:&nbsp;</strong>BA CHỮ M &ndash; C&ocirc;ng thức đầu tư&nbsp;kh&ocirc;ng c&oacute; rủi ro</p>\r\n\r\n<p><strong>Chương 5:</strong>&nbsp;Payback Time nghĩa l&agrave;&nbsp;&ldquo;KH&Ocirc;NG SỢ H&Atilde;I&rdquo;</p>\r\n\r\n<p><strong>Chương 6:&nbsp;</strong>T&Aacute;M BƯỚC chập chững&nbsp;đi đến th&agrave;nh c&ocirc;ng</p>\r\n\r\n<p><strong>Chương 7:</strong>&nbsp;Chỉ c&oacute; phương ph&aacute;p S&Agrave;N &amp; TRẦN&nbsp;(FACs &ndash; FLOOR &amp; CEILINGs) gi&uacute;p bạn kiếm tiền</p>\r\n\r\n<p><strong>Chương 8:</strong>&nbsp;C&acirc;u chuyện cổ t&iacute;ch của một&nbsp;gia đ&igrave;nh</p>\r\n\r\n<p><strong>Chương 9:</strong>&nbsp;TIỀN CH&Ugrave;A c&ugrave;ng Berky</p>\r\n\r\n<p><strong>Chương 10:</strong>&nbsp;C&ugrave;ng tham gia c&aacute;ch mạng&nbsp;về QUY TẮC SỐ 1.</p>\r\n', '299000', 2, 1, 5, 'bwr9jdcm.png', '2020-11-10', 'admin', '2020-12-10', 'admin', 1, 12, 12);
INSERT INTO `book` (`id`, `name`, `description`, `price`, `quantity`, `special`, `sale_off`, `picture`, `created`, `created_by`, `modified`, `modified_by`, `status`, `ordering`, `category_id`) VALUES
(24, 'Điều quan trọng nhất', '<p>Trong một mớ &ldquo;v&agrave;ng thau lẫn lộn&rdquo; của những cuốn s&aacute;ch về chứng kho&aacute;n, c&oacute; một cuốn s&aacute;ch kinh điển g&acirc;y ảnh hưởng s&acirc;u sắc tới h&agrave;ng triệu nh&agrave; đầu tư tr&ecirc;n thế giới đ&oacute; ch&iacute;nh l&agrave; cuốn s&aacute;ch&nbsp;<em>Điều Quan Trọng Nhất &ndash; The Most Important Thing</em>&nbsp;của Howard Marks. Cuốn s&aacute;ch n&agrave;y thực sự được coi l&agrave; một cuốn cẩm nang về đầu tư chứng kho&aacute;n n&oacute;i chung v&agrave; đầu tư gi&aacute; trị (value investing) n&oacute;i ri&ecirc;ng.</p>\r\n\r\n<p>Howard Marks&nbsp;&ndash; Chủ tịch ki&ecirc;m đồng s&aacute;ng lập của Quỹ đầu tư Oak Tree Capital, vốn dự định viết cuốn s&aacute;ch n&agrave;y khi &ocirc;ng nghỉ hưu. Nhưng huyền thoại đầu tư Warren Buffett ngưỡng mộ Howard Marks đến nỗi, &ocirc;ng đ&atilde; đề nghị viết lời giới thiệu cho cuốn s&aacute;ch n&agrave;y nếu Marks đồng &yacute; xuất bản n&oacute; sớm hơn. V&agrave; kết quả l&agrave;, ch&uacute;ng ta đ&atilde; c&oacute; th&ecirc;m một cuốn s&aacute;ch &ldquo;hiếm c&oacute; v&agrave; cực kỳ hữu &iacute;ch&rdquo; như lời Buffett.</p>\r\n\r\n<p>Với lối viết r&otilde; r&agrave;ng, mạch lạc v&agrave; đầy t&iacute;nh thuyết phục, Howard Marks đ&atilde; tr&igrave;nh b&agrave;y to&agrave;n bộ những quan điểm của &ocirc;ng về những điều &ocirc;ng coi l&agrave; quan trọng nhất khi đầu tư chứng kho&aacute;n như:</p>\r\n\r\n<p>+ Những cặp phạm tr&ugrave; về gi&aacute; cả &amp; gi&aacute; trị<br />\r\n+ Nhận thức về chu kỳ kinh tế<br />\r\n+ Suy nghĩ về việc đi ngược tr&agrave;o lưu khi đầu tư<br />\r\n+ Việc tr&aacute;nh xa c&aacute;c cạm bẫy v&agrave; đặc biệt l&agrave; về nhận thức rủi ro v&agrave; quản trị rủi ro khi đầu tư chứng kho&aacute;n.</p>\r\n\r\n<p>L&agrave; một trong những nh&agrave; đầu tư th&agrave;nh c&ocirc;ng nhất trong thế hệ của m&igrave;nh, Howard Marks lu&ocirc;n lu&ocirc;n nhấn mạnh sự th&agrave;nh c&ocirc;ng của &ocirc;ng gắn liền với những &aacute;m ảnh về việc nhận thức c&aacute;c cạm bẫy v&agrave; quản trị rủi ro đối với c&aacute;c khoản đầu tư của m&igrave;nh.</p>\r\n\r\n<p>Đương nhi&ecirc;n, cuốn s&aacute;ch&nbsp;<em>Điều Quan Trọng Nhất&nbsp;</em>sẽ kh&ocirc;ng khiến cho bạn gi&agrave;u hơn một c&aacute;ch nhanh ch&oacute;ng. N&oacute; kh&ocirc;ng phải l&agrave; cuốn s&aacute;ch sẽ &ldquo;vẽ vời&rdquo; cho bạn những đồ thị hay charts v&agrave; những ph&acirc;n t&iacute;ch t&igrave;m kiếm gi&aacute; trị về cổ phiếu chuy&ecirc;n s&acirc;u. Đ&acirc;y cũng kh&ocirc;ng phải l&agrave; một cuốn s&aacute;ch cầm tay chỉ việc (howto book).&nbsp;Nhưng điều gi&aacute; trị nhất m&agrave; cuốn s&aacute;ch n&agrave;y mang lại cho h&agrave;ng triệu nh&agrave; đầu tư tr&ecirc;n thế giới đ&oacute; ch&iacute;nh l&agrave;&nbsp;những nguy&ecirc;n tắc bất biến để kiểm nghiệm lại những giả định đầu tư của ch&iacute;nh họ, ngăn ngừa họ về những thứ nguy hiểm chết người trong đầu tư như: việc qu&aacute; tự tin, nghe theo đ&aacute;m đ&ocirc;ng khi đầu tư, sự nguy hiểm của việc kh&ocirc;ng biết m&igrave;nh, kh&ocirc;ng biết cũng như kh&ocirc;ng x&aacute;c định được vị thế v&agrave; chỗ đứng của m&igrave;nh trong một chu k&igrave; kinh tế.</p>\r\n\r\n<h2>Howard Marks l&agrave; ai?</h2>\r\n\r\n<p>Howard Marks</p>\r\n\r\n<p>Howard Marks l&agrave; một trong những nh&agrave; đầu tư gi&aacute; trị th&agrave;nh c&ocirc;ng nhất hiện nay. &Ocirc;ng l&agrave; đồng s&aacute;ng lập ki&ecirc;m Chủ tịch quản l&yacute; Quỹ đầu tư Oaktree Capital Management. Quỹ Oaktree hiện đang quản l&yacute; khối t&agrave;i sản trị gi&aacute; hơn 80 tỷ USD. Trước đ&oacute;, &ocirc;ng từng l&agrave; CIO của TCW. Warren Buffett đ&atilde; từng n&oacute;i về Howard Marks thế n&agrave;y: &ldquo;Khi nh&igrave;n thấy thư của Howard Marks trong h&ograve;m thư, đ&oacute; sẽ l&agrave; bức thư đầu ti&ecirc;n m&agrave; t&ocirc;i mở ra v&agrave; đọc&rdquo;.</p>\r\n\r\n<h2>Ai n&ecirc;n đọc cuốn s&aacute;ch Điều Quan Trọng Nhất?</h2>\r\n\r\n<p>&ndash; Bất cứ ai muốn học hỏi v&agrave; cải thiện kỹ năng đầu tư từ một bậc thầy đầu tư xuất sắc.<br />\r\n&ndash; Những nh&agrave; đầu tư nghi&ecirc;m t&uacute;c với số tiền xương m&aacute;u của m&igrave;nh<br />\r\n&ndash; Những nh&agrave; đầu tư muốn t&igrave;m kiếm những cơ hội vượt trội so với đ&aacute;m đ&ocirc;ng.</p>\r\n\r\n<h2>Review của c&aacute;c độc giả quốc tế về s&aacute;ch Điều Quan Trọng Nhất?</h2>\r\n\r\n<p>S&aacute;ch được đ&aacute;nh gi&aacute; 4,5/ 5 sao tại Amazon với 172 nhận x&eacute;t</p>\r\n\r\n<h2>C&aacute;c chuy&ecirc;n gia n&oacute;i g&igrave; về s&aacute;ch?</h2>\r\n\r\n<p><em>Khi nh&igrave;n thấy c&aacute;c bản ghi nhớ từ Howard Marks trong thư của m&igrave;nh, ch&uacute;ng l&agrave; thứ đầu ti&ecirc;n t&ocirc;i mở ra đọc. T&ocirc;i lu&ocirc;n học được điều g&igrave; đ&oacute;, v&agrave; ch&uacute;ng tăng gấp đ&ocirc;i qua cuốn s&aacute;ch Điều Quan Trọng Nhất của anh ấy.</em></p>\r\n\r\n<p>&ndash; Warren Buffett, Chủ tịch v&agrave; Gi&aacute;m đốc điều h&agrave;nh Berkshire Hathaway</p>\r\n\r\n<p><em>&Iacute;t quyển s&aacute;ch đầu tư n&agrave;o c&oacute; thể đạt ti&ecirc;u chuẩn như của Howard Marks với Điều quan trọng nhất. Cuốn s&aacute;ch&nbsp;Điều Quan Trọng Nhất chứa đựng sự kh&ocirc;n ngoan, d&iacute; dỏm buộc với g&oacute;c nh&igrave;n lịch sử. Nếu bạn đang t&igrave;m kiếm c&aacute;ch thức tr&aacute;nh khỏi những cạm bẫy đầu tư, bạn phải đọc cuốn s&aacute;ch n&agrave;y.</em></p>\r\n\r\n<p>&ndash; John C. Bogle, Nguy&ecirc;n Chủ tịch v&agrave; Nh&agrave; s&aacute;ng lập Tập đo&agrave;n Vanguard</p>\r\n\r\n<p><em>Những người nhận c&aacute;c bản ghi nhớ đầu tư thường xuy&ecirc;n của Howard Mark h&aacute;o hức chờ đợi sự xuất hiện của ch&uacute;ng với những sự thật cốt l&otilde;i v&agrave; hiểu biết độc đ&aacute;o. V&agrave; giờ đ&acirc;y sự kh&ocirc;n ngoan v&agrave; kinh nghiệm của nh&agrave; đầu tư tuyệt vời n&agrave;y đ&atilde; c&oacute; mặt cho tất cả mọi người. Điều Quan Trọng Nhất l&agrave; triết l&yacute; đầu tư được kiểm chứng qua thời gian của Mark v&agrave; cũng l&agrave; cuốn s&aacute;ch phải đọc d&agrave;nh cho tất cả c&aacute;c nh&agrave; đầu tư.</em></p>\r\n\r\n<p>&ndash; Seth A. Klarman, Chủ tịch tập đo&agrave;n Baupost</p>\r\n\r\n<h2>Mục lục</h2>\r\n\r\n<p>ĐIỀU QUAN TRỌNG NHẤT L&Agrave;&hellip;<br />\r\n07&nbsp;Lời tựa<br />\r\n11&nbsp;Lời giới thiệu<br />\r\n19&nbsp;Tư duy cấp độ 2<br />\r\n31&nbsp;Hiểu về thị trường hiệu quả (v&agrave; những hạn chế của n&oacute;)<br />\r\n49&nbsp;Gi&aacute; trị<br />\r\n65&nbsp;Mối quan hệ giữa gi&aacute; cả v&agrave; gi&aacute; trị<br />\r\n79&nbsp;Hiểu về rủi ro<br />\r\n109&nbsp;Nhận thức rủi ro<br />\r\n131&nbsp;Kiểm so&aacute;t rủi ro<br />\r\n149&nbsp;Lưu &yacute; về c&aacute;c chu kỳ<br />\r\n161&nbsp;Nhận thức về con lắc<br />\r\n175&nbsp;Chống lại những ảnh hưởng ti&ecirc;u cực<br />\r\n197&nbsp;Đi ngược tr&agrave;o lưu<br />\r\n215&nbsp;T&igrave;m kiếm m&oacute;n hời<br />\r\n231&nbsp;Ki&ecirc;n nhẫn chờ đợi cơ hội<br />\r\n249&nbsp;Biết những g&igrave; bạn kh&ocirc;ng biết<br />\r\n265&nbsp;C&oacute; &yacute; thức về nơi ch&uacute;ng ta đứng<br />\r\n281&nbsp;Đ&aacute;nh gi&aacute; vai tr&ograve; của may mắn<br />\r\n297&nbsp;Đầu tư ph&ograve;ng thủ<br />\r\n321&nbsp;Tr&aacute;nh c&aacute;c cạm bẫy<br />\r\n347&nbsp;Th&ecirc;m gi&aacute; trị<br />\r\n361&nbsp;Nh&oacute;m tất cả lại với nhau</p>\r\n', '288000', 5, 0, 5, 'q7dha1nc.png', '2020-11-10', 'admin', '2020-12-10', 'admin', 1, 13, 12),
(25, 'Thương vụ để đời', '<p>Harry Dent l&agrave; một nh&agrave; kinh tế học được cho l&agrave; dị thường so với nhiều nh&agrave; kinh tế học hiện nay. &Ocirc;ng l&agrave; người theo quan điểm chu kỳ, đặc biệt l&agrave; c&aacute;c chu kỳ nh&acirc;n khẩu học sẽ chi phối đến chu kỳ bong b&oacute;ng v&agrave; đổ vỡ của nền kinh tế v&agrave; thị trường t&agrave;i ch&iacute;nh. Thương vụ để đời&nbsp;tr&igrave;nh b&agrave;y quan điểm của Dent về khả năng xuất hiện c&uacute; đỡ vỡ si&ecirc;u bong b&oacute;ng trong năm 2017-2019 dựa tr&ecirc;n xu hướng nh&acirc;n khẩu học của h&agrave;ng loạt quốc gia lớn như Hoa Kỳ, Trung Quốc, Nhật Bản, Đức đang bị gi&agrave; h&oacute;a.</p>\r\n\r\n<p>Th&ocirc;ng qua cuốn s&aacute;ch n&agrave;y, Harry Dent đ&atilde; mang đến cho bạn đọc hiểu biết&nbsp;về c&aacute;c chu kỳ quan trọng c&oacute; ảnh hưởng đến đời sống con người, hoạt động kinh tế v&agrave; thị trường t&agrave;i ch&iacute;nh như chu kỳ đổi mới c&ocirc;ng nghệ, chu kỳ địa ch&iacute;nh trị v&agrave; chu kỳ vệt đen mặt trời. Đ&acirc;y l&agrave; bốn chu kỳ lớn m&agrave; theo Dent sẽ gi&uacute;p bạn nhận ra được kh&uacute;c cua ở ph&iacute;a trước hay những bước ngoặt quan trọng của nền kinh tế.</p>\r\n\r\n<p>Thương vụ để đời</p>\r\n\r\n<p>Bằng c&aacute;ch tr&igrave;nh b&agrave;y những nghi&ecirc;n cứu học thuật sau hơn 30 năm nghi&ecirc;n cứu về c&aacute;c bong b&oacute;ng, Harry Dent đ&uacute;c kết những c&ocirc;ng thức gi&aacute; trị, đơn giản v&agrave; dễ hiểu gi&uacute;p c&aacute;c nh&agrave; đầu tư nhanh ch&oacute;ng nhận diện sự tồn tại của bong b&oacute;ng.&nbsp;<strong>Nh&agrave; đầu tư c&oacute; thể sử dụng c&aacute;c quy tắc đơn giản nhưng hữu hiệu n&agrave;y để tư duy về bối cảnh kinh tế t&agrave;i ch&iacute;nh hiện nay.</strong>&nbsp;Liệu c&oacute; phải chăng ch&uacute;ng ta đang ở trong một si&ecirc;u bong b&oacute;ng lớn nhất trong lịch sử? Tại sao c&aacute;c nh&agrave; quản l&yacute; ch&iacute;nh s&aacute;ch, l&atilde;nh đạo quốc gia, ng&acirc;n h&agrave;ng trung ương, c&aacute;c nh&agrave; ph&acirc;n t&iacute;ch danh tiếng lu&ocirc;n l&ecirc;n tiếng chối bỏ sự tồn tại của bong b&oacute;ng?</p>\r\n\r\n<p>Dựa tr&ecirc;n g&oacute;c nh&igrave;n lịch sử về c&aacute;c bong b&oacute;ng xuy&ecirc;n suốt lịch sử t&agrave;i ch&iacute;nh hiện đại: từ bong b&oacute;ng hoa tulip, bong b&oacute;ng SouthSea cho đến c&aacute;c bong b&oacute;ng ở Hoa Kỳ hay c&aacute;c bong b&oacute;ng từng xuất hiện khắp thế giới trong những thập ni&ecirc;n 90, nh&agrave; đầu tư r&uacute;t ra được những b&agrave;i học gi&aacute; trị về nguồn gốc h&igrave;nh th&agrave;nh v&agrave; hậu quả của c&aacute;c bong b&oacute;ng. Từ đ&oacute;, gi&uacute;p nh&agrave; đầu tư c&oacute; được tầm nh&igrave;n quan trọng về diễn biến v&agrave; kết cục của bong b&oacute;ng hiện nay.</p>\r\n\r\n<p>Đ&uacute;ng như phong c&aacute;ch nổi loạn thường thấy, Harry Dent đ&atilde; mạnh dạn bước v&agrave;o một lĩnh vực m&agrave; hầu hết c&aacute;c nh&agrave; kinh tế học thường n&eacute; tr&aacute;nh.&nbsp;<strong>Đ&oacute; l&agrave; đưa ra những hướng dẫn gi&uacute;p nh&agrave; đầu tư t&igrave;m kiếm lợi nhuận dựa tr&ecirc;n những đ&aacute;nh gi&aacute; về chu kỳ v&agrave; bong b&oacute;ng hiện nay.</strong>&nbsp;Dự b&aacute;o tương lai l&agrave; một c&ocirc;ng việc kh&oacute; khăn v&agrave; kh&ocirc;ng thể tr&aacute;nh khỏi sai s&oacute;t (đ&oacute; l&agrave; l&yacute; do tại sao c&aacute;c chuy&ecirc;n gia thường n&eacute; tr&aacute;nh) v&igrave; vậy kh&ocirc;ng n&ecirc;n đ&aacute;nh gi&aacute; dựa tr&ecirc;n kết quả. Điều quan trọng l&agrave; những cơ sở nghi&ecirc;n cứu v&agrave; đ&oacute; l&agrave; điều m&agrave; t&ocirc;i đang nh&igrave;n thấy được từ Harry Dent.</p>\r\n\r\n<p>David Stockman, một nh&agrave; kinh doanh nổi tiếng, một ch&iacute;nh trị gia h&agrave;ng đầu v&agrave; một t&aacute;c giả nổi tiếng kết luận:&nbsp;<em>&ldquo;Đ&acirc;y l&agrave; cuốn s&aacute;ch m&agrave; c&aacute;c nh&agrave; đầu tư, doanh nh&acirc;n phải đọc&rdquo;</em>&nbsp;nếu như muốn t&igrave;m kiếm cơ hội tạo dựng sự gi&agrave;u c&oacute; từ khủng hoảng. Khủng hoảng t&agrave;i ch&iacute;nh l&agrave; cơ hội tuyệt vời m&agrave; c&aacute;c doanh nh&acirc;n, nh&agrave; đầu tư nổi tiếng như &ocirc;ng tr&ugrave;m dầu mỏ John D.Rockefeller (doanh nh&acirc;n gi&agrave;u c&oacute; nhất trong lịch sử Hoa Kỳ), vua th&eacute;p Andrew Carnegie, &ocirc;ng tr&ugrave;m ng&acirc;n h&agrave;ng J.P.Morgan, v&agrave; &ocirc;ng vua ng&agrave;nh đường sắt Edward Harriman từng tận dụng để x&acirc;y dựng cơ nghiệp gi&agrave;u sang tột bậc.</p>\r\n\r\n<p>Liệu một lần nữa, Harry Dent, nh&agrave; kinh tế từng dự b&aacute;o ch&iacute;nh x&aacute;c thập ni&ecirc;n mất m&aacute;t của Nhật Bản v&agrave; giai đoạn thịnh vượng của Hoa Kỳ v&agrave;o những năm 1990, đỉnh&nbsp;<strong><a href=\"https://happy.live/bong-bong-dotcom-tham-hoa-hay-dieu-ky-cua-kinh-te-gioi-phan-1/\">bong b&oacute;ng Dotcom&nbsp;</a></strong>2000, đỉnh bong b&oacute;ng nợ dưới chuẩn 2008 c&oacute; gi&uacute;p nh&agrave; đầu tư sinh tồn v&agrave; trở n&ecirc;n thịnh vượng từ si&ecirc;u bong b&oacute;ng 2017-2019.</p>\r\n\r\n<p><strong>H&atilde;y đọc cuốn s&aacute;ch n&agrave;y v&agrave; t&igrave;m kiếm những&nbsp;</strong><strong><a href=\"https://happy.live/san-pham/sach-thuong-vu-de-doi/\">thương vụ để đời</a></strong><strong>&nbsp;v&igrave; n&oacute; chỉ xuất hiện một lần trong đời người!!!</strong></p>\r\n\r\n<p>Thương vụ để đời Harry Dent</p>\r\n\r\n<hr />\r\n<h2><strong>NHỮNG LỜI KHEN D&Agrave;NH CHO THƯƠNG VỤ ĐỂ ĐỜI</strong></h2>\r\n\r\n<p><em>&ldquo;Cuốn s&aacute;ch n&agrave;y cho thấy Harry l&agrave; một chuy&ecirc;n gia am hiểu về bong b&oacute;ng xuy&ecirc;n suốt to&agrave;n bộ lịch sử v&agrave; l&yacute; giải cho ch&uacute;ng ta hiểu tại so đang c&oacute; bong b&oacute;ng lớn chưa từng thấy tr&ecirc;n to&agrave;n cầu. Đ&acirc;y l&agrave; cuốn s&aacute;ch phải đọc.&rdquo;</em></p>\r\n\r\n<p><strong>DAVID STOCKMAN,</strong>&nbsp;cựu gi&aacute;m đốc văn ph&ograve;ng Quản l&yacute; v&agrave; Ng&acirc;n s&aacute;ch (OMB) Hoa Kỳ dưới thời Tổng thống Ronald Reagan, t&aacute;c giả cuốn s&aacute;ch nổi tiếng The Great Deformation</p>\r\n\r\n<p><em>&ldquo;Cuốn s&aacute;ch mới cho thấy, Harry c&ograve;n l&agrave; một chuy&ecirc;n gia về chu kỳ v&agrave; c&aacute;c bong b&oacute;ng xuy&ecirc;n suốt to&agrave;n bộ lịch sử. Kh&ocirc;ng c&oacute; thời điểm n&agrave;o quan trọng như b&acirc;y giờ để hiểu về t&aacute;c động của bong b&oacute;ng lớn nhất lịch sử hiện đại đến của cải v&agrave; sự nghiệp của bạn. T&ocirc;i khuyến kh&iacute;ch bạn đọc cuốn s&aacute;ch n&agrave;y&rdquo;</em></p>\r\n\r\n<p><strong>JOHN MAULDIN</strong>, t&aacute;c giả cuốn s&aacute;ch nổi tiếng Code Red and Endgame</p>\r\n\r\n<p>&ldquo;<em>Harry Dent đ&atilde; l&agrave;m một c&ocirc;ng việc tuyệt vời khi minh họa một c&aacute;ch dễ hiểu c&aacute;c bong b&oacute;ng trong lịch sử, v&iacute; dụ bong b&oacute;ng Mississippi v&agrave; Louisiana do John Law tạo ra v&agrave;o thế kỷ 18, l&agrave; tương đồng với bối cảnh hiện nay như thế n&agrave;o. Cuốn s&aacute;ch n&agrave;y được viết rất hay v&agrave; gi&uacute;p bạn đọc trải nghiệm từ từ&rdquo;</em></p>\r\n\r\n<p><strong>LACY HUNT</strong>, nguy&ecirc;n nh&agrave; kinh tế trưởng của HSBC Group</p>\r\n\r\n<p><em>&ldquo;Trong cuốn s&aacute;ch n&agrave;y, Harry kh&ocirc;ng chỉ đưa ra những nguy&ecirc;n tắc hướng dẫn nhận diện bong b&oacute;ng, m&agrave; c&ograve;n r&uacute;t ra những b&agrave;i học từ c&aacute;c bong b&oacute;ng trong lịch sử, điều đ&oacute; thật tuyệt vời! Một lần nữa, &ocirc;ng đ&atilde; n&oacute;i điều n&agrave;y một c&aacute;ch ch&iacute;nh x&aacute;c, trước khi ch&uacute;ng ta biết rằng n&oacute; sẽ ch&iacute;nh x&aacute;c. Một cuốn s&aacute;ch tuyệt vời&rdquo;</em></p>\r\n\r\n<p><strong>DAVID HAYS</strong>, chủ tịch v&agrave; nh&agrave; tư vấn cao cấp của Comprehensive Financial Consutants</p>\r\n\r\n<hr />\r\n<h2><strong>MỤC LỤC</strong></h2>\r\n\r\n<h3><strong>PHẦN GIỚI THIỆU- BONG B&Oacute;NG: TẠI SAO CH&Uacute;NG TA KH&Ocirc;NG BAO GIỜ NH&Igrave;N THẤY N&Oacute;</strong></h3>\r\n\r\n<h3><strong>PHẦN I- L&Agrave;M THẾ N&Agrave;O ĐỂ NHẬN DIỆN BONG B&Oacute;NG</strong></h3>\r\n\r\n<p><strong>Chương 1</strong>&ndash; Làm th&ecirc;́ nào đ&ecirc;̉ nhận diện bong bóng: nguy&ecirc;n tắc hướng d&acirc;̃n s&ocirc;́ 1</p>\r\n\r\n<p><strong>Chương 2</strong>&ndash; Làm th&ecirc;́ nào đ&ecirc;̉ nhận diện bong bóng: nguy&ecirc;n tắc hướng d&acirc;̃n s&ocirc;́ 2</p>\r\n\r\n<p><strong>Chương 3</strong>&ndash; Làm th&ecirc;́ nào để nhận diện bong bóng: nguy&ecirc;n tắc hướng d&acirc;̃n s&ocirc;́ 3</p>\r\n\r\n<p><strong>Chương 4</strong>&ndash; Làm th&ecirc;́ nào đ&ecirc;̉ nhận diện bong bóng: nguy&ecirc;n tắc hướng d&acirc;̃n s&ocirc;́ 4.</p>\r\n\r\n<p><strong>Chương 5</strong>&ndash; Làm th&ecirc;́ nào đ&ecirc;̉ nh&acirc;̣n di&ecirc;̣n bong bóng: nguy&ecirc;n tắc hướng d&acirc;̃n s&ocirc;́ 5, 6 và 7.</p>\r\n\r\n<h3>PH&Acirc;̀N II- M&Ocirc;̣T M&Ocirc; TẢ NGẮN GỌN (NHƯNG XUY&Ecirc;N SU&Ocirc;́T) TO&Agrave;N BỘ LỊCH SỬ CÁC CU&Ocirc;̣C BONG BÓNG</h3>\r\n\r\n<p>Chương 6- Những bài học từ lịch sử.</p>\r\n\r\n<p>Chương 7- Những bong bóng ở hoa kỳ.</p>\r\n\r\n<p>Chương 8- Bong bóng những năm 90 và hiện nay.</p>\r\n\r\n<h3><strong>PH&Acirc;̀N III- QUY&Ecirc;̀N NĂNG DỰ BÁO</strong></h3>\r\n\r\n<p><strong>Chương 9</strong>&ndash; Làm th&ecirc;́ nào dự báo thời điểm bong bóng sụp đ&ocirc;̉</p>\r\n\r\n<p><strong>Chương 10</strong>&ndash; Chu kỳ địa ch&iacute;nh trị: khi kh&oacute; khăn&hellip;n&oacute; thật kinh khủng.</p>\r\n\r\n<p><strong>Chương 11</strong>&ndash; B&ugrave;ng nổ, đổ vỡ v&agrave; sức mạnh đổi mới c&ocirc;ng nghệ.</p>\r\n\r\n<p><strong>Chương 12</strong>&ndash; Bằng chứng về sự hồi phục vớ vẩn</p>\r\n\r\n<p><strong>Chương 13</strong>&ndash; Đổ vỡ bong b&oacute;ng gi&uacute;p nh&agrave; đầu tư huyền thoại l&agrave;m gi&agrave;u như thế n&agrave;o&hellip;v&agrave; bạn c&oacute; thể l&agrave; một trong số họ.</p>\r\n\r\n<h3><strong>PHẦN IV- BONG B&Oacute;NG V&Agrave; ĐỢT T&Aacute;I THIẾT LỚN NHẤT TRONG CUỘC ĐỜI CH&Uacute;NG TA</strong></h3>\r\n\r\n<p><strong>Chương 14- L</strong>ực kinh tế mạnh nhất m&agrave; thế giới chưa từng biết đến.</p>\r\n\r\n<p><strong>Chương 15</strong>&ndash; Bong b&oacute;ng nợ v&agrave; t&agrave;i ch&iacute;nh lớn nhất trong lịch sử.</p>\r\n\r\n<p><strong>Chương 16</strong>&ndash; Tổng nợ của hoa kỳ đang ở mức cao nguy hiểm.</p>\r\n\r\n<p><strong>Chương 17</strong>&ndash; Bong bóng ở trung qu&ocirc;́c đại lục</p>\r\n\r\n<p><strong>Chương 18</strong>&ndash; Bong b&oacute;ng bất động sản chưa từng c&oacute; tiền lệ của trung quốc.</p>\r\n\r\n<p><strong>Chương 19</strong>&ndash; Tại sao và khi nào bong bóng nợ và tài sản tài chính toàn c&acirc;̀u sẽ đ&ocirc;̉ vỡ.</p>\r\n\r\n<p><strong>Chương 20</strong>&ndash; Định thời điểm v&agrave; hướng dẫn chi tiết</p>\r\n\r\n<h3><strong>PH&Acirc;̀N V &ndash; T&Igrave;M KIẾM LỢI NHUẬN TỪ C&Aacute;C THƯƠNG VỤ Đ&Ecirc;̉ ĐỜI</strong></h3>\r\n\r\n<p><strong>Chương 21</strong>&ndash; T&igrave;m kiếm lợi nhuận từ hoạt đ&ocirc;̣ng đ&acirc;̀u tư</p>\r\n\r\n<p><strong>Chương 22</strong>&ndash; T&igrave;m kiếm lợi nhuận từ hoạt đ&ocirc;̣ng kinh doanh</p>\r\n\r\n<p><strong>Chương 23</strong>&ndash; T&igrave;m kiếm lợi nhuận từ kinh doanh b&acirc;́t đ&ocirc;̣ng sản.</p>\r\n\r\n<p><strong>Chương 24</strong>&ndash; T&igrave;m kiếm lợi nhuận từ các thị trường mới n&ocirc;̉i</p>\r\n\r\n<hr />\r\n<h2><strong>Đ&Ocirc;I N&Eacute;T VỀ T&Aacute;C GIẢ</strong></h2>\r\n\r\n<p><strong><a href=\"https://en.wikipedia.org/wiki/Harry_Dent\">Harry Dent</a></strong>&nbsp;sinh ra ở Columbia, Bắc Carolina, l&agrave; con trai của ch&iacute;nh trị gia Harry S.Dent. Dent nghi&ecirc;n cứu kinh tế học v&agrave;o những năm 70 tại Harvard.</p>\r\n\r\n<p>Dent l&agrave; nh&agrave; s&aacute;ng lập của c&ocirc;ng ty đầu tư HS Dent Investment Management c&oacute; trụ sở tại Tampa (Florida), chuy&ecirc;n cung cấp dịch vụ tư vấn, dự b&aacute;o thị trường v&agrave; quỹ hỗ tương Dent Strategic Portfolio Fund Mutual. Dent cũng l&agrave; chủ tịch v&agrave; l&agrave; nh&agrave; s&aacute;ng lập của Dent Research v&agrave; H.S Dent Publishing.</p>\r\n\r\n<p>L&yacute; thuyết đầu tư của Dent chủ yếu dựa v&agrave;o S&oacute;ng Chi Ti&ecirc;u (Thế Hệ) v&agrave; c&aacute;c chu kỳ kh&aacute;c. &Ocirc;ng l&agrave; người theo quan điểm chu kỳ. S&oacute;ng Chi Ti&ecirc;u cho rằng người ti&ecirc;u d&ugrave;ng l&agrave; đối tượng ch&iacute;nh chi phối nền kinh tế, thị trường t&agrave;i ch&iacute;nh, bất động sản v&agrave; v&agrave;ng. L&yacute; thuyết n&agrave;y cho rằng, chu kỳ của nền kinh tế v&agrave; thị trường t&agrave;i ch&iacute;nh (<strong>bong b&oacute;ng v&agrave; sụp đỗ</strong>) l&agrave; do chu kỳ trong h&agrave;nh vi ti&ecirc;u d&ugrave;ng của người ti&ecirc;u d&ugrave;ng theo từng độ tuổi.</p>\r\n', '199000', 5, 0, 5, 'td9r7kfl.png', '2020-11-10', 'admin', '2020-12-10', 'admin', 1, 14, 12),
(26, 'Ichimoku charts', '<p>Hệ thống giao dịch Ichimoku Kinko Hyo hay c&ograve;n gọi l&agrave; &ldquo;Đồ thị c&aacute;i nh&igrave;n tho&aacute;ng qua về sự c&acirc;n bằng của thị trường&rdquo; từ l&acirc;u đ&atilde; trở th&agrave;nh một trong những hệ thống giao dịch rất phổ biến được c&aacute;c nh&agrave; đầu tư từ phương T&acirc;y đến &Aacute; Ch&acirc;u ứng dụng một c&aacute;ch hiệu quả trong c&aacute;c thị trường từ cổ phiếu, đến tr&aacute;i phiếu, v&agrave;ng, h&agrave;ng h&oacute;a cơ bản, thị trường ph&aacute;i sinh, hợp đồng quyền chọn hay thị trường ngoại hối FX (Forex)&hellip;</p>\r\n\r\n<p>Với người Nhật, hệ thống n&agrave;y từng l&agrave; hệ thống xương sống trong ph&acirc;n t&iacute;ch kỹ thuật tại tất cả c&aacute;c ph&ograve;ng giao dịch, c&aacute;c c&ocirc;ng ty chứng kho&aacute;n v&agrave; c&aacute;c định chế t&agrave;i ch&iacute;nh. Tất cả c&aacute;c dealer cũng như trader ở Nhật đều sử dụng th&agrave;nh thạo v&agrave; th&ocirc;ng thuộc với c&ocirc;ng cụ n&agrave;y. V&agrave; n&oacute; cũng l&agrave; gi&aacute;o tr&igrave;nh vỡ l&ograve;ng khi bước v&agrave;o nghề đầu tư, kinh doanh đối với người Nhật.</p>\r\n\r\n<p>C&ograve;n với người phương T&acirc;y, kỹ thuật n&agrave;y chỉ trở n&ecirc;n phổ biến kể từ năm 2007, khi lần đầu ti&ecirc;n một cuốn s&aacute;ch bằng tiếng Anh viết về hệ thống n&agrave;y được xuất bản, sau khi t&aacute;c giả của n&oacute; đ&atilde; c&oacute; 10 năm nghi&ecirc;n cứu v&agrave; sử dụng hệ thống ph&acirc;n t&iacute;ch Ichimoku Clouds n&agrave;y tại c&aacute;c ph&ograve;ng giao dịch của Nhật, Anh Quốc v&agrave; tr&ecirc;n to&agrave;n thế giới. Đ&oacute; ch&iacute;nh l&agrave; cuốn s&aacute;ch của t&aacute;c giả Nicole Elliott<em>&nbsp;<strong>&ldquo;Hệ thống giao dịch Ichimoku Charts &ndash;&nbsp;Ichimoku Chart&rdquo;</strong></em></p>\r\n\r\n<p>Trong khi đ&oacute; ở Việt Nam, kỹ thuật v&agrave; sự hiểu biết về hệ thống giao dịch n&agrave;y mới được phổ biến trong khoảng v&agrave;i năm trở lại đ&acirc;y, những t&agrave;i liệu về n&oacute; c&ograve;n rất &iacute;t v&agrave; nhiều thiếu s&oacute;t, chủ yếu l&agrave; tự ph&aacute;t theo h&igrave;nh thức &ldquo;người dạy người, nghề dạy nghề&rdquo; chứ chưa c&oacute; một s&aacute;ch vở ch&iacute;nh thống n&agrave;o về hệ thống n&agrave;y.</p>\r\n\r\n<p>Đ&oacute; ch&iacute;nh l&agrave; l&yacute; do th&ocirc;i th&uacute;c ch&uacute;ng t&ocirc;i mua bản quyền v&agrave; xuất bản cuốn s&aacute;ch&nbsp;<strong><em>&ldquo;Hệ thống giao dịch Ichimoku Charts&rdquo;</em>&nbsp;</strong>của t&aacute;c giả Nicole Elliott tại Việt Nam, với phi&ecirc;n bản mới nhất năm 2018 c&oacute; chỉnh sửa v&agrave; bổ sung.</p>\r\n\r\n<h2>Nội dung cuốn s&aacute;ch?</h2>\r\n\r\n<p>&ndash; Nội dung hấp dẫn nhất của s&aacute;ch&nbsp;<em><strong>Hệ Thống Giao Dịch Ichimoku Charts</strong></em>&nbsp;l&agrave; 3 nguy&ecirc;n tắc ch&iacute;nh của Ichi, gồm l&yacute; thuyết s&oacute;ng chỉ ra c&aacute;ch gh&eacute;p s&oacute;ng I, V, N hay Y, P như thế n&agrave;o, l&yacute; thuyết mục ti&ecirc;u gi&aacute; chỉ ra c&aacute;ch đo đạc mục ti&ecirc;u gi&aacute; trong ph&acirc;n t&iacute;ch, hoặc l&yacute; thuyết thời gian đưa ra v&agrave; l&yacute; giải c&aacute;c con số 9, 17, 26 cho c&aacute;c dấu hiệu đảo chiều về mặt thời gian.</p>\r\n\r\n<p>&ndash; &Yacute; nghĩa của những h&igrave;nh th&aacute;i đ&aacute;m m&acirc;y d&agrave;y, mỏng ra sao, c&ocirc;ng dụng thế n&agrave;o, m&acirc;y tương lai chỉ điều g&igrave;&hellip;.</p>\r\n\r\n<p>&ndash; C&aacute;c quan hệ của Ichimoku với biểu đồ nến Nhật.</p>\r\n\r\n<p>&ndash; C&aacute;c trường hợp giao dịch mẫu (case studies) xuy&ecirc;n suốt, gi&uacute;p nhận diện c&aacute;ch thức giao dịch với Ichimoku m&agrave; kh&ocirc;ng phải bối rối khi đọc qu&aacute; nhiều phần l&yacute; thuyết.</p>\r\n\r\n<h2>Cuốn s&aacute;ch n&agrave;y d&agrave;nh cho ai?</h2>\r\n\r\n<p>&ndash; S&aacute;ch ph&ugrave; hợp cho những người mới cũng như những trader đang đi t&igrave;m c&aacute;c phương ph&aacute;p mới cho chiến lược của m&igrave;nh trong thị trường chứng kho&aacute;n, forex, v&agrave;ng, bitcoin&hellip;</p>\r\n\r\n<p>&ndash; S&aacute;ch đặc biệt ph&ugrave; hợp cho những người vốn đang sử dụng đồ thị h&igrave;nh nến, những người muốn mở mang kiến thức v&agrave; kỹ thuật về hệ thống giao dịch Ichimoku Cloud.</p>\r\n\r\n<h2>Cuốn s&aacute;ch được cấu tạo như thế n&agrave;o?</h2>\r\n\r\n<p>Cuốn s&aacute;ch bao gồm 8 chương ch&iacute;nh:</p>\r\n\r\n<p><em><strong>Chương 1: Lịch sử</strong></em></p>\r\n\r\n<p>Sơ lược về lịch sử của nến Nhật v&agrave; sự ph&aacute;t triển của hệ thống giao dịch Ichimoku Kinko Hyo.</p>\r\n\r\n<p><strong><em>Chương 2: C&aacute;c cấu th&agrave;nh của đồ thị đ&aacute;m m&acirc;y (cloud charts)</em></strong></p>\r\n\r\n<p>Giới thiệu nhanh về đồ thị nến Nhật &ndash; nền tảng của Ichimoku &ndash; v&agrave; giải th&iacute;ch chi tiết về c&aacute;ch thức đồ thị Ichimoku Kinko Hyo được cấu th&agrave;nh như thế n&agrave;o.</p>\r\n\r\n<p><em><strong>Chương 3: Diễn giải &yacute; nghĩa của c&aacute;c đ&aacute;m m&acirc;y</strong></em></p>\r\n\r\n<p>L&agrave;m thế n&agrave;o để diễn giải c&aacute;c đồ thị đ&aacute;m m&acirc;y, bao gồm: X&aacute;c định những ngưỡng hỗ trợ v&agrave; c&aacute;c ngưỡng kh&aacute;ng cự, sự quan trọng của độ d&agrave;y của đ&aacute;m m&acirc;y v&agrave; khoảng c&aacute;ch giữa gi&aacute; v&agrave; đ&aacute;m m&acirc;y, v&agrave; l&agrave;m thế n&agrave;o để c&oacute; được những &ldquo;m&aacute;nh&rdquo; c&oacute; được những vị thế giao dịch tốt nhất.</p>\r\n\r\n<p><em><strong>Chương 4: Ba nguy&ecirc;n l&yacute;</strong></em></p>\r\n\r\n<p>Thảo luận về ba nguy&ecirc;n l&yacute; của đồ thị Ichimoku Kinko Hyo: Nguy&ecirc;n l&yacute; s&oacute;ng, gi&aacute; mục ti&ecirc;u v&agrave; nguy&ecirc;n l&yacute; khoảng thời gian.</p>\r\n\r\n<p><em><strong>Chương 5: C&aacute;c case study (th&iacute; dụ minh họa)</strong></em></p>\r\n\r\n<p>Một v&agrave;i c&aacute;c case study được lồng gh&eacute;p v&agrave;o trong cuốn s&aacute;ch để giải th&iacute;ch chi tiết về những đồ thị của Ichimoku.</p>\r\n\r\n<p><em><strong>Chương 6: Giao dịch ph&aacute;i sinh (Options với Ichimoku)</strong></em></p>\r\n\r\n<p>L&agrave;m thế n&agrave;o để kết hợp, lồng gh&eacute;p được yếu tố thời gian v&agrave; c&aacute;c mức gi&aacute; cho ph&ugrave; hợp với hệ thống ph&acirc;n t&iacute;ch của Ichimoku, đặc biệt d&agrave;nh ri&ecirc;ng cho c&aacute;c chiến lược trading quyền chọn (options).</p>\r\n\r\n<p><em><strong>Chương 7: Cập nhật về c&aacute;ch hiện tại t&ocirc;i đang sử dụng đồ thị Ichimoku ng&agrave;y nay</strong></em></p>\r\n\r\n<p>Một g&oacute;c nh&igrave;n to&agrave;n cảnh về c&aacute;ch thức sử dụng c&aacute;c đồ thị đ&aacute;m m&acirc;y đ&atilde; ph&aacute;t triển, cải tiến kể từ khi ấn bản đầu ti&ecirc;n của cuốn s&aacute;ch ra đời năm 2007, c&aacute;ch đ&acirc;y 11 năm trước.</p>\r\n\r\n<p><em><strong>Chương 8: Case study mới</strong></em></p>\r\n\r\n<p>Một th&iacute; dụ về c&aacute;ch sử dụng ph&acirc;n t&iacute;ch đồ thị ng&agrave;y nay. Ch&uacute;ng ta cũng sẽ đi qua một loạt những đồ thị ng&agrave;y v&agrave; đồ thị tuần m&agrave; kh&ocirc;ng cần hiểu những g&igrave; đang diễn ra ở tr&ecirc;n thị trường.</p>\r\n\r\n<h2>T&aacute;c giả Nicole Elliott l&agrave; ai?</h2>\r\n\r\n<p>Nicole Elliott tốt nghiệp Trường Kinh tế London v&agrave; l&agrave; th&agrave;nh vi&ecirc;n của Hiệp hội Ph&acirc;n t&iacute;ch Kỹ thuật. C&ocirc; đ&atilde; l&agrave;m việc tại&nbsp;London&nbsp;trong hơn 20 năm với nhiều vị tr&iacute; kh&aacute;c nhau cho c&ocirc;ng việc Ph&acirc;n t&iacute;ch kỹ thuật tại c&aacute;c ng&acirc;n h&agrave;ng quốc tế lớn.</p>\r\n\r\n<p>L&agrave; nh&agrave; ph&acirc;n t&iacute;ch cao cấp của Mizuho Corporate Bank tại London, c&ocirc; d&ugrave;ng phương ph&aacute;p của m&igrave;nh để bao qu&aacute;t thị trường ngoại hối, l&atilde;i suất v&agrave; thị trường h&agrave;ng h&oacute;a v&agrave; chỉ số vốn cổ phần.</p>\r\n\r\n<h2>C&aacute;c chuy&ecirc;n gia n&oacute;i v&igrave; về s&aacute;ch?</h2>\r\n\r\n<p>&nbsp;&ldquo;Cho d&ugrave; bạn l&agrave; người giao dịch ngắn hạn (short-term trader) hay l&agrave; nh&agrave; đầu tư d&agrave;i hạn (long-term investor) c&oacute; sử dụng c&ocirc;ng cụ ph&acirc;n t&iacute;ch kỹ thuật, th&igrave; Hệ thống giao dịch Ichimoku Kinko Hyo thực sự hữu dụng để bổ sung v&agrave;o việc ph&acirc;n t&iacute;ch kỹ thuật của bạn. B&ecirc;n ngo&agrave;i những c&ocirc;ng cụ phần mềm vẽ chart Ichimoku hiện hữu ng&agrave;y nay th&igrave; cuốn s&aacute;ch n&agrave;y của Nicole l&agrave; tất cả những g&igrave; bạn cần!&rdquo;</p>\r\n\r\n<p><em>DOMINIC PICARDA -Former Columnist and writer, Investors Chronicle and FT Weekend</em></p>\r\n\r\n<p>&ldquo;Nếu bạn muốn học hỏi về hệ thống Ichimoku Kinko Hyo, t&ocirc;i sẽ kh&ocirc;ng muốn giới thiệu cho bạn cuốn s&aacute;ch n&agrave;o kh&aacute;c về chủ đề n&agrave;y ngoại trừ cuốn s&aacute;ch n&agrave;y của Nicole Elliot (B&agrave; ấy đơn giản l&agrave; qu&aacute; tuyệt vời, b&agrave; thực sự đ&atilde; giao dịch rất nhiều bằng hệ thống n&agrave;y tại Nhật v&agrave; đ&atilde; được chỉ bảo kỹ lưỡng về hệ thống n&agrave;y bởi những Traders c&oacute; tiếng người Nhật&rdquo;.</p>\r\n', '200000', 5, 0, 5, 'r68qbvc4.png', '2020-11-10', 'admin', '2020-12-10', 'admin', 1, 15, 12),
(27, 'Từ tốt đến vĩ đại ', '', '148000', 3, 0, 5, 'sgpolf74.png', '2020-11-10', 'admin', '2020-11-10', NULL, 1, 14, 15),
(32, 'Hệ thông bán hàng đỉnh cao', '<p><strong>&ldquo;Hệ thống b&aacute;n h&agrave;ng đỉnh cao&rdquo;</strong>&nbsp;gồm nhiều chiến lược, c&ocirc;ng cụ v&agrave; chiến thuật hoạt động c&ugrave;ng nhau như một hệ thống t&iacute;ch hợp v&agrave; tự củng cố. Phương ph&aacute;p được tr&igrave;nh b&agrave;y theo từng bước cụ thể để thực h&agrave;nh v&agrave; trở n&ecirc;n giỏi hơn. D&ugrave; bạn c&oacute; năng lực ra sao, tố chất thế n&agrave;o bạn cũng c&oacute; thể trở th&agrave;nh một người b&aacute;n h&agrave;ng tuyệt vời th&ocirc;ng qua việc &ldquo;thực h&agrave;nh c&oacute; chủ &yacute;&rdquo; theo hệ thống n&agrave;y.</p>\r\n\r\n<p>Bản chất của&nbsp;<strong>&ldquo;Hệ thống b&aacute;n h&agrave;ng đỉnh cao&rdquo;</strong>&nbsp;l&agrave; l&agrave;m cho kh&aacute;ch h&agrave;ng cảm thấy họ đặc biệt. Bằng c&aacute;ch lắng nghe những mong muốn của họ v&agrave; đ&aacute;p ứng những mong muốn đ&oacute;. Khi đ&atilde; khiến kh&aacute;ch h&agrave;ng cảm thấy bạn coi trọng họ rồi, phần c&ograve;n lại của qu&aacute; tr&igrave;nh b&aacute;n h&agrave;ng sẽ trở n&ecirc;n dễ d&agrave;ng hơn.</p>\r\n\r\n<p>Cuối c&ugrave;ng kh&aacute;ch h&agrave;ng của bạn sẽ h&agrave;i l&ograve;ng đến nỗi họ kh&ocirc;ng thể dừng n&oacute;i về bạn với mọi người. Họ trở th&agrave;nh người quảng c&aacute;o cho bạn &ndash; một c&aacute;ch marketing hiệu quả hơn bao giờ hết.</p>\r\n\r\n<p>Trong cuốn s&aacute;ch bạn sẽ kh&aacute;m ph&aacute; thấy Hệ thống b&aacute;n h&agrave;ng&nbsp;đỉnh cao ở những phương diện sau:</p>\r\n\r\n<p>1) Rất to&agrave;n diện &ndash; Bao gồm tất cả những kỹ năng bạn cần để&nbsp;th&agrave;nh c&ocirc;ng trong nghề sẽ được giới thiệu.</p>\r\n\r\n<p>2) C&aacute;c phương ph&aacute;p được dựa tr&ecirc;n nghi&ecirc;n cứu khoa học &ndash; C&aacute;c&nbsp;c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu sẽ được l&agrave;m nổi bật v&agrave; bạn sẽ hiểu tại sao&nbsp;phương ph&aacute;p ph&aacute;t triển kinh doanh hay b&aacute;n h&agrave;ng, tư vấn của Mo&nbsp;Bunnell lại c&oacute; hiệu quả.</p>\r\n\r\n<p>3) C&aacute;c phương ph&aacute;p b&aacute;n h&agrave;ng rất thật! Bởi v&igrave; bản chất cốt l&otilde;i&nbsp;nhất của ch&uacute;ng l&agrave; x&acirc;y dựng những mối quan hệ, c&aacute;c mối li&ecirc;n kết với&nbsp;kh&aacute;ch h&agrave;ng. Do đ&oacute;, bạn sẽ thấy c&ocirc;ng việc của bạn đang l&agrave;m thật&nbsp;th&uacute; vị v&agrave; rất c&oacute; &yacute; nghĩa.</p>\r\n\r\n<h2>AI N&Ecirc;N ĐỌC BỘ S&Aacute;CH N&Agrave;Y</h2>\r\n\r\n<p>&ndash; Chủ doanh nghiệp, quản l&yacute; marketing, những người l&agrave;m marketing hoặc l&agrave; người mới khởi nghiệp.</p>\r\n\r\n<p>&ndash; Những người đang trực tiếp l&agrave;m c&aacute;c c&ocirc;ng việc b&aacute;n h&agrave;ng như: nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng B2C, B2B (Business to Business), B2G (Business to Government), chuy&ecirc;n vi&ecirc;n ph&aacute;t triển kinh doanh, tư vấn gi&aacute;o dục, tư vấn bảo hiểm, tư vấn b&aacute;n xe hơi, m&ocirc;i giới bất động sản, m&ocirc;i giới chứng kho&aacute;n&hellip;<br />\r\n&ndash; Những người giỏi về một chuy&ecirc;n m&ocirc;n bất kỳ v&agrave; muốn kh&aacute;ch h&agrave;ng trả tiền nhiều hơn để bạn gi&uacute;p họ thực hiện chuy&ecirc;n m&ocirc;n đ&oacute;: gi&aacute;o vi&ecirc;n, luật sư, chuy&ecirc;n vi&ecirc;n kế to&aacute;n, thợ chụp h&igrave;nh đ&aacute;m cưới, chuy&ecirc;n vi&ecirc;n thiết kế đồ họa, marketing&hellip;<br />\r\n&ndash; Những nh&agrave; quản l&yacute;, chủ doanh nghiệp muốn cải thiện kết quả kinh doanh của c&ocirc;ng ty.</p>\r\n', '250000', 3, 1, 5, 'pctefqbr.png', '2020-11-10', 'admin', '2020-12-10', 'admin', 1, 15, 13),
(33, 'Siêu cò', '<p>Một c&acirc;u n&oacute;i nổi tiếng trong tiếng T&acirc;y Ban Nha &ldquo;<em>N&oacute;i cho t&ocirc;i biết bạn chơi với ai, v&agrave; t&ocirc;i sẽ n&oacute;i cho bạn biết bạn l&agrave; người thế n&agrave;o</em>&rdquo; hay Người Việt cũng c&oacute; rất nhiều c&acirc;u n&oacute;i tương tự: &ldquo;<em>Đi với bụt mặc &aacute;o c&agrave; sa, đi với ma mặc &aacute;o giấy&rdquo;</em>.</p>\r\n\r\n<p>B&iacute; quyết của người th&agrave;nh c&ocirc;ng, đơn giản l&agrave; &ldquo;chọn bạn m&agrave; chơi&rdquo;. Họ chọn chơi với những người giỏi hơn, kh&ocirc;ng phải để lợi dụng, m&agrave; l&agrave; c&ugrave;ng ph&aacute;t triển v&agrave; th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<p>C&acirc;u hỏi d&agrave;nh cho c&aacute;c doanh nh&acirc;n hiện nay kh&ocirc;ng phải l&agrave;: &ldquo;<em>L&agrave;m c&aacute;ch n&agrave;o t&ocirc;i c&oacute; thể kết nối được với nhiều người hơn?</em>&rdquo; m&agrave; l&agrave; &ldquo;<em>L&agrave;m thế n&agrave;o t&ocirc;i x&aacute;c định v&agrave; nu&ocirc;i dưỡng những kết nối quan trọng sẽ th&uacute;c đẩy th&agrave;nh c&ocirc;ng của t&ocirc;i?</em>&rdquo;, &ldquo;<em>L&agrave;m c&aacute;ch n&agrave;o t&ocirc;i c&oacute; thể kết nối với mọi người để họ nhận được cuộc gọi của t&ocirc;i v&agrave; gi&uacute;p đỡ t&ocirc;i khi cần?</em>&rdquo;.</p>\r\n\r\n<p>Trong quyển s&aacute;ch&nbsp;<strong>Si&ecirc;u C&ograve; &ndash; C&aacute;ch thức biến quan hệ th&agrave;nh tiền tệ</strong>, Judy Robinett người được biết đến với danh hiệu &ldquo;<em>người phụ nữ với th&ocirc;ng tin li&ecirc;n hệ hạng bạch kim</em>&rdquo; sẽ chia sẻ đến bạn những b&iacute; mật Si&ecirc;u Kết Nối &ndash; X&acirc;y dựng c&aacute;c mối quan hệ chiến lược, c&oacute; gi&aacute; trị cao, gi&uacute;p bạn th&agrave;nh c&ocirc;ng nhanh hơn trong kinh doanh như sau:</p>\r\n\r\n<ul>\r\n	<li>X&aacute;c định, l&ecirc;n kế hoạch tiếp cận v&agrave; chăm s&oacute;c 155 mối quan hệ quan trọng, c&oacute; gi&aacute; trị nhất gi&uacute;p bạn th&agrave;nh c&ocirc;ng l&acirc;u d&agrave;i</li>\r\n	<li>Tương t&aacute;c v&agrave; tạo ra những &ldquo;đề xuất gi&aacute; trị&rdquo; c&oacute; t&iacute;nh thuyết phục để những kết nối h&agrave;ng đầu ch&uacute; &yacute; đến bạn</li>\r\n	<li>C&aacute;ch gia tăng &ldquo;vốn quan hệ&rdquo; v&agrave; &ldquo;vốn x&atilde; hội&rdquo; tạo n&ecirc;n c&aacute;c t&agrave;i sản dựa tr&ecirc;n cơ sở ph&aacute;t triển, duy tr&igrave;, nu&ocirc;i dưỡng c&aacute;c mối quan hệ chất lượng cao</li>\r\n	<li>Nắm vững 4 b&iacute; mật kết nối c&oacute; gi&aacute; trị cao: h&agrave;o ph&oacute;ng, quan t&acirc;m, chu đ&aacute;o v&agrave; lu&ocirc;n gia tăng gi&aacute; trị cho c&aacute;c mối quan hệ chất lượng</li>\r\n	<li>Giao tiếp tinh tế, cuốn h&uacute;t người nghe dựa tr&ecirc;n sở th&iacute;ch v&agrave; nhu cầu của họ</li>\r\n	<li>Sử dụng phương tiện truyền th&ocirc;ng x&atilde; hội hiệu quả để tiếp cận những người c&oacute; sức ảnh hưởng v&agrave; kh&oacute; tiếp cận nhất</li>\r\n</ul>\r\n\r\n<h2>Giới thiệu t&aacute;c giả</h2>\r\n\r\n<p>Judy Robinett đ&atilde; xuất hiện tr&ecirc;n Forbes, Venture Beat, Huffington Post v&agrave; Bloomberg Businessweek như một v&iacute; dụ điển h&igrave;nh của một người &ldquo;Si&ecirc;u Kết Nối&rdquo; &ndash; Người sử dụng mạng lưới v&agrave; kinh nghiệm của m&igrave;nh để tăng tốc độ ph&aacute;t triển v&agrave; n&acirc;ng cao lợi nhuận kinh doanh.</p>\r\n\r\n<p>Trong hơn 30 năm l&agrave;m kinh doanh v&agrave; l&atilde;nh đạo, Robinett nằm trong hội đồng tư vấn của Illuminate Ventures, Pereg Ventures, Springboard Enterprises, Women Innovate Mobile v&agrave; nắm quyền quản l&yacute; c&aacute;c c&ocirc;ng ty trong danh s&aacute;ch Fortune 500.</p>\r\n\r\n<p>C&ocirc; từng l&agrave; gi&aacute;m đốc điều h&agrave;nh của Golden Seeds Angel Network; CEO s&agrave;n giao dịch Medical Discoveries v&agrave; l&agrave; giảng vi&ecirc;n của chương tr&igrave;nh 10.000 Small Business của Goldman Sachs.</p>\r\n\r\n<p>Robinett đ&atilde; c&oacute; hơn 300 b&agrave;i ph&aacute;t biểu tr&ecirc;n to&agrave;n thế giới d&agrave;nh cho c&aacute;c kh&aacute;n giả tại MIT, BIO Europe, CalPoly, AT&amp;T, Westinghouse v&agrave; Bộ Năng lượng Mỹ.</p>\r\n\r\n<h2>Ai n&ecirc;n đọc cuốn s&aacute;ch n&agrave;y:</h2>\r\n\r\n<ul>\r\n	<li>Doanh nh&acirc;n, nh&agrave; quản l&yacute;, l&atilde;nh đạo nh&oacute;m, chuy&ecirc;n vi&ecirc;n sale, chuy&ecirc;n vi&ecirc;n bất động sản, nh&agrave; tư vấn, nh&oacute;m hỗ trợ chuy&ecirc;n m&ocirc;n, nh&agrave; cung cấp sản phẩm/dịch vụ muốn th&agrave;nh c&ocirc;ng hơn trong sự nghiệp</li>\r\n	<li>Nh&agrave; huy động vốn, quản l&yacute; quỹ đầu tư, m&ocirc;i giới chứng kho&aacute;n, t&agrave;i ch&iacute;nh muốn t&igrave;m kiếm v&agrave; tiếp cận những mối quan hệ c&oacute; gi&aacute; trị cao</li>\r\n	<li>Những bạn trẻ đam m&ecirc; đọc s&aacute;ch, ph&aacute;t triển bản th&acirc;n, y&ecirc;u th&iacute;ch kết nối l&agrave;m nền tảng thăng tiến trong tương lai</li>\r\n	<li>V&agrave; tất cả những ai đang khao kh&aacute;t kết nối, th&agrave;nh c&ocirc;ng, gi&agrave;u c&oacute; v&agrave; hạnh ph&uacute;c hơn bằng c&aacute;c mối quan hệ chất lượng</li>\r\n</ul>\r\n\r\n<p>Độc giả n&oacute;i g&igrave; về cuốn s&aacute;ch n&agrave;y</p>\r\n\r\n<p>Cuốn s&aacute;ch đang được đ&aacute;nh gi&aacute; 4.8/5 sao tr&ecirc;n Amazon.com</p>\r\n\r\n<p><strong><em>&ldquo;C&oacute; rất nhiều hướng dẫn c&aacute;ch &ldquo;kết nối c&aacute;c mối quan hệ&rdquo; v&agrave; quyển s&aacute;ch n&agrave;y đối với t&ocirc;i m&agrave; n&oacute;i n&oacute; c&ograve;n hơn cả một cuốn s&aacute;ch hướng dẫn. N&oacute; một cuốn s&aacute;ch đ&aacute;ng tham khảo. Trước khi t&ocirc;i bắt đầu &aacute;p dụng một hệ thống theo d&otilde;i, trước khi t&ocirc;i tham dự một hội nghị, trước khi t&ocirc;i tiếp cận một người c&oacute; ảnh hưởng cao cấp kh&aacute;c, t&ocirc;i sẽ đọc lại c&aacute;c chương li&ecirc;n quan.</em></strong></p>\r\n\r\n<p><strong><em>T&ocirc;i cảm thấy rằng những người đang cố gắng để th&agrave;nh c&ocirc;ng trong kinh doanh v&agrave; những người chưa đọc cuốn s&aacute;ch n&agrave;y đang gặp bất lợi khi kh&ocirc;ng biết được c&aacute;c b&iacute; mật trong quyển s&aacute;ch Si&ecirc;u C&ograve;. Lời khuy&ecirc;n của t&ocirc;i: h&atilde;y nhanh tay mua v&agrave; đọc n&oacute; sớm nhất c&oacute; thể.&rdquo;</em></strong></p>\r\n\r\n<p>Dennis Coates &ndash; T&aacute;c giả, diễn giả v&agrave; CEO của Performance Support System</p>\r\n\r\n<p><strong><em>Cuốn s&aacute;ch Si&ecirc;u C&ograve; sẽ gi&uacute;p bạn trở th&agrave;nh chuy&ecirc;n gia si&ecirc;u kết nối. T&aacute;c giả Judy Robinett chia sẻ quy tắc 5 + 50 + 100 để đảm bảo bạn biết ai l&agrave; người chủ chốt trong mạng lưới quan hệ của bạn. T&ocirc;i may mắn c&oacute; được một mạng lưới rộng lớn gồm những người tuyệt vời v&agrave; những gợi &yacute; thiết thực của Judy đ&atilde; gi&uacute;p t&ocirc;i suy nghĩ một c&aacute;ch chiến lược về họ. Tr&ecirc;n hết, Judy rất h&agrave;o ph&oacute;ng với mạng lưới của ch&iacute;nh m&igrave;nh v&agrave; khuyến kh&iacute;ch bạn cũng vậy; nếu bạn vẫn c&ograve;n e ngại v&agrave; d&egrave; dặt, đừng bận t&acirc;m. Đ&acirc;y l&agrave; quyển s&aacute;ch đ&aacute;ng đọc.</em></strong></p>\r\n\r\n<p>Michele Wucker &ndash; T&aacute;c giả THE GRAY RHINO</p>\r\n\r\n<h2>Mục lục</h2>\r\n\r\n<p><strong>Chương 1</strong>: Sức mạnh của những mối quan hệ gi&aacute; trị cao v&agrave; c&oacute; chiến lược</p>\r\n\r\n<p><strong>Chương 2</strong>: Bạn b&egrave;, đồng minh v&agrave; những người kết nối quyền năng</p>\r\n\r\n<p><strong>Chương 3</strong>: Khiến mạng lưới của bạn trở n&ecirc;n Rộng, S&acirc;u v&agrave; Mạnh mẽ</p>\r\n\r\n<p><strong>Chương 4</strong>: Những hệ sinh th&aacute;i ph&ugrave; hợp sẽ quyết định th&agrave;nh c&ocirc;ng của bạn</p>\r\n\r\n<p><strong>Chương 5</strong>: Tư duy của người Si&ecirc;u Kết Nối</p>\r\n\r\n<p><strong>Chương 6</strong>: Giai đoạn 1: Chuẩn bị để Si&ecirc;u Kết Nối</p>\r\n\r\n<p><strong>Chương 7</strong>: Giai đoạn 2: Nhắm mục ti&ecirc;u, Kết nối v&agrave; Gắn kết &ndash; H&atilde;y tối đa h&oacute;a thời gian của bạn với mọi kết nối</p>\r\n\r\n<p><strong>Chương 8</strong>: Giai đoạn 3: Kết nối lại, Đ&aacute;nh gi&aacute; v&agrave; K&iacute;ch hoạt, v&agrave; nh&acirc;n Gi&aacute; trị l&ecirc;n gấp nhiều lần</p>\r\n\r\n<p><strong>Chương 9</strong>: Giai đoạn 4: Kết nối c&aacute;c kết nối của bạn để th&ecirc;m th&agrave;nh c&ocirc;ng</p>\r\n\r\n<p><strong>Chương 10</strong>: Những chiến lược kết nối quyền năng cho hội thảo v&agrave; truyền th&ocirc;ng mạng x&atilde; hội</p>\r\n\r\n<p><strong>Chương 11</strong>: Phụ nữ kết nối: X&acirc;y dựng th&agrave;nh c&ocirc;ng một mối quan hệ</p>\r\n\r\n<p><strong>Chương 12</strong>: Những kết nối quyền lực l&agrave; đầu ti&ecirc;n phải kết nối</p>\r\n', '203000', 3, 1, 5, 'npbtohid.png', '2020-11-10', 'admin', '2020-12-10', 'admin', 1, 19, 13);
INSERT INTO `book` (`id`, `name`, `description`, `price`, `quantity`, `special`, `sale_off`, `picture`, `created`, `created_by`, `modified`, `modified_by`, `status`, `ordering`, `category_id`) VALUES
(34, 'Nuốt cá lớn', '<h2>Kẻ Th&aacute;ch thức l&agrave; ai?</h2>\r\n\r\n<p>Giữa &ldquo;cuộc chiến&rdquo; thị phần ng&agrave;y c&agrave;ng khốc liệt của thị trường, c&aacute;c doanh nghiệp kh&ocirc;ng ngừng tung ra &ldquo;chi&ecirc;u tr&ograve;&rdquo; để thu h&uacute;t kh&aacute;ch h&agrave;ng. V&agrave;i thập kỷ qua ch&uacute;ng ta đ&atilde; chứng kiến nhiều trận chiến kinh điển giữa thương hiệu nhỏ v&agrave; thương hiệu lớn c&oacute; tiếng.</p>\r\n\r\n<p>Trong cuốn s&aacute;ch Nuốt C&aacute; Lớn, t&aacute;c giả Adam Morgan ph&acirc;n t&iacute;ch những case đối đầu thực chiến của c&aacute;c thương hiệu nhỏ v&agrave; vừa nhưng với một kh&aacute;i niệm rất mới đ&oacute; l&agrave; Thương hiệu Th&aacute;ch thức &ndash; những thương hiệu nhỏ b&eacute; c&ocirc;ng khai th&aacute;ch thức thương hiệu đang thống trị thị trường, c&ocirc;ng khai theo đuổi những ti&ecirc;u chuẩn đo lường mới, c&aacute;ch tạo dấu ấn mới, khao kh&aacute;t đồng cảm v&agrave; khao kh&aacute;t doanh số kinh doanh.</p>\r\n\r\n<h2>Đặc điểm nhận dạng Thương hiệu Th&aacute;ch thức tr&ecirc;n thị trường:</h2>\r\n\r\n<p>&ndash; Được định nghĩa kh&ocirc;ng phải l&agrave; Thương hiệu Đứng đầu, đồng thời cũng kh&ocirc;ng hoạt động trong c&aacute;c thị trường ng&aacute;ch<br />\r\n&ndash; C&oacute; tham vọng vượt xa tiềm lực marketing hiện c&oacute;<br />\r\n&ndash; Sẵn s&agrave;ng chấp nhận c&aacute;c kết quả marketing xảy ra do sự ch&ecirc;nh lệch tham vọng v&agrave; tiềm lực marketing hiện c&oacute;<br />\r\n&ndash; Đạt chuẩn tỉ lệ th&agrave;nh c&ocirc;ng</p>\r\n\r\n<p>Một Thương hiệu Th&aacute;ch thức c&oacute; thể đối đầu với Thương hiệu Dẫn đầu khi thương hiệu ấy l&agrave;m cho m&igrave;nh kh&aacute;c biệt được với c&aacute;c thương hiệu kh&aacute;c trong c&ugrave;ng ng&agrave;nh h&agrave;ng với nhận diện đặc trưng cụ thể.</p>\r\n\r\n<h2>Cuốn s&aacute;ch Nuốt c&aacute; lớn c&oacute; g&igrave; đặc biệt?</h2>\r\n\r\n<p>T&aacute;c giả Adam Morgan đ&atilde; đưa ra những lời khuy&ecirc;n thực tế c&ugrave;ng những v&iacute; dụ đa dạng, dễ l&agrave;m theo để cho người đọc thấy những Thương hiệu Th&aacute;ch thức đ&atilde; gi&agrave;nh lấy sự ch&uacute; &yacute; v&agrave; &ldquo;lấy cắp&rdquo; kh&aacute;ch h&agrave;ng từ những đối thủ cạnh tranh c&oacute; ng&acirc;n s&aacute;ch quảng c&aacute;o v&agrave; marketing lớn hơn họ nhiều lần như thế n&agrave;o.<br />\r\nĐồng thời giới thiệu 8 Nguy&ecirc;n tắc Th&aacute;ch thức &ndash; nhấn mạnh v&agrave;o việc mang lại một c&aacute;i nh&igrave;n mới về thị trường; x&acirc;y dựng một Nhận diện nổi bật, thu h&uacute;t v&agrave; đầy cảm x&uacute;c; triển khai một chiến lược truyền th&ocirc;ng c&oacute; t&iacute;nh th&acirc;m nhập; v&agrave; tập trung ch&uacute; &yacute; v&agrave;o c&aacute;c &yacute; tưởng hơn l&agrave; người ti&ecirc;u d&ugrave;ng.</p>\r\n\r\n<h2>T&aacute;c giả Adam Morgan l&agrave; ai?</h2>\r\n\r\n<p>Adam Morgan l&agrave; Gi&aacute;m đốc điều h&agrave;nh của eatbigfish (www.eatbigfish.com) &ndash; một thương hiệu tư vấn marketing to&agrave;n cầu chuy&ecirc;n về chiến lược, h&agrave;nh vi v&agrave; văn h&oacute;a của Thương hiệu Th&aacute;ch thức. Từng nằm trong ban điều h&agrave;nh của TBWA\\Chiat\\Day, một trong những c&ocirc;ng ty quảng c&aacute;o lớn nhất thế giới, &ocirc;ng c&oacute; cơ hội l&agrave;m việc qua với những kh&aacute;ch h&agrave;ng như IKEA, Ulilever, Virgin v&agrave; Apple.<br />\r\nMorgan v&agrave; đồng sự đ&atilde; c&ugrave;ng nhau tạo ra Dự &Aacute;n Kẻ Th&aacute;ch Thức (The Challenger project) để nghi&ecirc;n cứu về c&aacute;ch tư duy v&agrave; h&agrave;nh vi của những thương hiệu Th&aacute;ch thức dựa tr&ecirc;n những điều họ nghĩ, n&oacute;i v&agrave; viết ra thị trường.</p>\r\n\r\n<h2>Ai n&ecirc;n đọc cuốn s&aacute;ch n&agrave;y?</h2>\r\n\r\n<p>Cuốn s&aacute;ch n&agrave;y d&agrave;nh cho những &ldquo;con c&aacute; b&eacute;&rdquo; &ndash; doanh nghiệp vừa v&agrave; nhỏ muốn:</p>\r\n\r\n<ul>\r\n	<li>Thay đổi tư duy tr&igrave; trệ, chậm ph&aacute;t triển trong bối cảnh truyền th&ocirc;ng đang chuyển biến mạnh mẽ về phương tiện, c&ocirc;ng nghệ v&agrave; web.</li>\r\n	<li>Khẳng định bản th&acirc;n l&agrave; một thương hiệu sống động, khỏe mạnh v&agrave; lu&ocirc;n khao kh&aacute;t trăng trưởng.</li>\r\n	<li>L&agrave;m đậm n&eacute;t đặc trưng kh&aacute;c biệt, gia tăng lợi nhuận của thương hiệu nhằm chống lại những đối thủ cạnh tranh lớn hơn.</li>\r\n</ul>\r\n\r\n<h2>Review của c&aacute;c độc giả quốc tế về s&aacute;ch Nuốt C&aacute; Lớn?</h2>\r\n\r\n<p>Nuốt C&aacute; Lớn thu h&uacute;t được nhiều độc giả, được<strong>&nbsp;đ&aacute;nh gi&aacute; 4/5 sao tr&ecirc;n Amazon</strong>&nbsp;&ndash; trang thương mại điện tử lớn nhất Thế giới.</p>\r\n\r\n<h2>Chuy&ecirc;n gia g&igrave; n&oacute;i về s&aacute;ch Nuốt c&aacute; lớn</h2>\r\n\r\n<p><strong><em>&ldquo;Lời khuy&ecirc;n về thương hiệu của Adam Morgan l&agrave; một trong những lời khuy&ecirc;n thực tế v&agrave; hữu dụng nhất. Nh&oacute;m của t&ocirc;i chịu tr&aacute;ch nhiệm gi&uacute;p đỡ những thương hiệu khử m&ugrave;i như Axe v&agrave; Degree đi đến th&agrave;nh c&ocirc;ng trong thị trường Mỹ, v&agrave; Nuốt C&aacute; Lớn đ&atilde; gi&uacute;p ch&uacute;ng t&ocirc;i định h&igrave;nh lại c&aacute;ch tư duy, hướng cả nh&oacute;m tập trung h&igrave;nh th&agrave;nh những h&agrave;nh vi &ldquo;Th&aacute;ch thức&rdquo; v&agrave; gi&uacute;p ch&uacute;ng t&ocirc;i tăng trưởng thị phần ấn tượng.&rdquo;</em></strong></p>\r\n\r\n<p>-Kevin George, Ph&oacute; Chủ tịch v&agrave; Tổng Gi&aacute;m đốc Unilever Mỹ</p>\r\n\r\n<p><strong><em>&ldquo;T&ocirc;i ngấu nghiến quyển s&aacute;ch n&agrave;y của Adam v&agrave; sử dụng c&aacute;ch tư duy của &ocirc;ng để tạo ra method &ndash; một Thương hiệu Th&aacute;ch thức chiến đấu chống lại những g&atilde; khổng lồ trong ng&agrave;nh c&ocirc;ng nghiệp x&agrave; ph&ograve;ng. N&oacute;i ch&uacute;ng t&ocirc;i sử dụng những quy tắc &ocirc;ng đề ra mỗi ng&agrave;y cũng vẫn chưa đủ. Quyển s&aacute;ch n&agrave;y sẽ cho bạn một khu&ocirc;n mẫu để luồn l&aacute;ch qua b&agrave;n ch&acirc;n của g&atilde; khổng lồ Goliath &ndash; v&agrave; tạo ra sức tăng trưởng kh&ocirc;ng ngừng.&rdquo;</em></strong></p>\r\n\r\n<p>-Eric Ryan, đồng s&aacute;ng lập thương hiệu method</p>\r\n\r\n<p><strong><em>&ldquo;&rsquo;Th&aacute;ch thức&rsquo; kh&ocirc;ng phải một trạng th&aacute;i kinh doanh, n&oacute; l&agrave; một trạng th&aacute;i về t&acirc;m l&yacute;. C&aacute;ch suy nghĩ n&agrave;y rất quyền lực v&agrave; quan trọng để đem lại th&agrave;nh c&ocirc;ng lớn cho thương hiệu cũng như cho c&aacute;c c&ocirc;ng ty khởi nghiệp, v&agrave; quyển s&aacute;ch n&agrave;y sẽ giải th&iacute;ch cho bạn l&yacute; do tại sao.&rdquo;</em></strong></p>\r\n\r\n<p>-Andy Berndt, Gi&aacute;m đốc điều h&agrave;nh, Google Creative Lab</p>\r\n\r\n<h2>MỤC LỤC</h2>\r\n\r\n<p>Lời tựa<br />\r\nLời n&oacute;i đầu<br />\r\nLời giới thiệu<br />\r\nLời mở đầu</p>\r\n\r\n<p><strong>PHẦN 1: Quy m&ocirc; v&agrave; đặc điểm của Con C&aacute; Lớn</strong><br />\r\nChương 1: Quy luật tăng l&atilde;i<br />\r\nChương 2: Người ti&ecirc;u d&ugrave;ng kh&ocirc;ng c&ograve;n như cũ nữa<br />\r\nChương 3: Thương hiệu Th&aacute;ch thức l&agrave; thương hiệu như thế n&agrave;o?</p>\r\n\r\n<p><strong>PHẦN 2: 8 Nguy&ecirc;n tắc Th&agrave;nh c&ocirc;ng của c&aacute;c Thương hiệu Th&aacute;ch thức</strong><br />\r\nChương 4: Nguy&ecirc;n tắc đầu ti&ecirc;n: Ng&acirc;y thơ một c&aacute;ch Th&ocirc;ng minh<br />\r\nChương 5: T&igrave;m ra điểm cốt l&otilde;i của thương hiệu<br />\r\nChương 6: Nguy&ecirc;n tắc thứ 2: X&acirc;y dựng Nhận diện Hải đăng<br />\r\nChương 7: Nguy&ecirc;n tắc thứ 3: D&agrave;nh lấy vị tr&iacute; Thống lĩnh Tư duy trong ng&agrave;nh h&agrave;ng<br />\r\nChương 8: Nguy&ecirc;n tắc thứ 4: Tạo ra những biểu tượng T&aacute;i đ&aacute;nh gi&aacute;<br />\r\nChương 9: Nguy&ecirc;n tắc thứ 5: Sự hy sinh<br />\r\nChương 10: Nguy&ecirc;n tắc thứ 6: Cam Kết Xa<br />\r\nChương 11: Nguy&ecirc;n tắc thứ 7: Sử dụng truyền th&ocirc;ng v&agrave; quảng c&aacute;o để th&acirc;m nhập văn ho&aacute; x&atilde; hội<br />\r\nChương 12: Nguy&ecirc;n tắc thứ 8: Lấy &yacute; tưởng l&agrave;m trung t&acirc;m, kh&ocirc;ng lấy người ti&ecirc;u d&ugrave;ng l&agrave;m trung t&acirc;m</p>\r\n\r\n<p><strong>PHẦN 3: Ứng dụng</strong><br />\r\nChương 13: L&ecirc;n kế hoạch h&agrave;nh động: Hai ng&agrave;y lập tr&igrave;nh tư duy<br />\r\nChương 14: C&ocirc;ng việc của người giữ ngọn hải đăng</p>\r\n\r\n<p><strong>PHẦN 4: Tư duy, văn ho&aacute; v&agrave; rủi ro</strong><br />\r\nChương 15: Thương hiệu Th&aacute;ch thức l&agrave; một c&aacute;ch tư duy: Giữ vị tr&iacute; số 1, tư duy như vị tr&iacute; số 2<br />\r\nChương 16: Rủi ro, &yacute; ch&iacute; v&agrave; v&ograve;ng tr&ograve;n bằng d&acirc;y thừng</p>\r\n\r\n<p>Lời cảm ơn cho lần t&aacute;i bản thứ II</p>\r\n', '107000', 5, 0, 5, '0ktoe5il.png', '2020-11-10', 'admin', '2020-12-10', 'admin', 1, 20, 13),
(35, 'Gen Z - Đọc vị thế hệ sống ảo', '<p><strong>Gen Z</strong>&nbsp;l&agrave; danh từ d&ugrave;ng để chỉ những người trẻ sinh từ năm 1996 &ndash; 2012. Sau giai đoạn b&ugrave;ng nổ của thế hệ Thi&ecirc;n ni&ecirc;n kỷ &ndash; Gen Y, Thế hệ Sống Ảo &ndash; Gen Z đ&atilde; tiếp bước v&agrave; trở th&agrave;nh những cư d&acirc;n đang l&ecirc;n của thời đại số.</p>\r\n\r\n<p>Họ l&agrave; một thế hệ đ&ocirc;ng đảo, đa dạng v&agrave; kh&aacute;c biệt nhất từ trước đến nay. Với khả năng tập trung chỉ 8 gi&acirc;y, sinh ra trong c&ocirc;ng nghệ, y&ecirc;u th&iacute;ch sự đơn giản, kh&ocirc;ng trung th&agrave;nh với bất kỳ thương hiệu n&agrave;o, Gen Z trở th&agrave;nh thế hệ định h&igrave;nh, dẫn dắt xu hướng ti&ecirc;u d&ugrave;ng trong tương lai.</p>\r\n\r\n<p><strong>Tại Việt Nam, thế hệ Gen Z đang chiếm hơn ⅙ d&acirc;n số với khoảng 14,4 triệu người</strong>&nbsp;&ndash; đ&acirc;y l&agrave; lực lượng lao động kế tiếp, cũng l&agrave; &ldquo;mỏ v&agrave;ng&rdquo; kh&aacute;ch h&agrave;ng tiềm năng cho tất cả những người l&agrave;m kinh doanh, dịch vụ, gi&aacute;o dục hay nh&agrave; tuyển dụng&hellip;</p>\r\n\r\n<p>Việc t&igrave;m hiểu những đặc điểm v&agrave; xu hướng h&agrave;nh vi của Gen Z, thấu hiểu họ nghĩ g&igrave;, l&agrave;m g&igrave;, th&iacute;ch g&igrave; sẽ l&agrave; ch&igrave;a kh&oacute;a để ta c&oacute; thể dự đo&aacute;n những xu hướng tương lai đang chờ đ&oacute;n.</p>\r\n\r\n<h1>L&agrave;m thế n&agrave;o tiếp cận Thế hệ&nbsp;Gen Z &ndash; Thế hệ &ldquo;V&agrave;ng&rdquo;; &ldquo;Check-in Sống Ảo&rdquo; với chi ph&iacute; thấp nhất?</h1>\r\n\r\n<p>Gen Z &ndash; Đọc vị thế hệ Sống Ảo l&agrave; một c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu c&ocirc;ng phu về c&aacute;c insight bổ &iacute;ch của Thế hệ Sống ảo &ndash; Gen Z. Với cuốn s&aacute;ch n&agrave;y bạn sẽ t&igrave;m được những phương thức thực sự hiệu quả để tiếp cận những kh&aacute;ch h&agrave;ng tiềm năng đầy th&uacute; vị n&agrave;y. Cuốn s&aacute;ch sẽ cho bạn thấy được:<br />\r\n&bull; C&aacute;ch Thế hệ Sống ảo &ndash; Gen Z suy nghĩ v&agrave; cư xử tr&ecirc;n m&ocirc;i trường số v&agrave; l&yacute; do cho những h&agrave;nh động đ&oacute;.<br />\r\n&bull; L&yacute; giải những tr&agrave;o lưu của Gen Z dưới g&oacute;c nh&igrave;n t&acirc;m l&yacute; thực tiễn v&iacute; dụ: Instagram, Tiktok, mua sắm online, d&ugrave;ng xe &ocirc;m c&ocirc;ng nghệ, phượt, tr&agrave; sữa&hellip;<br />\r\n&bull; G&oacute;c nh&igrave;n đa chiều trong c&aacute;ch suy nghĩ, t&acirc;m l&yacute; v&agrave; h&agrave;nh vi ra quyết định của Thế hệ Sống ảo &ndash; Gen Z tr&ecirc;n 8 phương diện: Kết Nối, Giải Tr&iacute;, Học Tập, C&ocirc;ng Việc, Mua Sắm, Di Chuyển, Ăn Uống v&agrave; Thời Trang.<br />\r\n&bull; Cẩm nang t&acirc;m l&yacute; cho c&aacute;c thương hiệu kinh doanh, ẩm thực, thời trang, c&ocirc;ng nghệ&hellip; trong cuộc chạy đua hướng đến kh&aacute;ch h&agrave;ng thế hệ Z.<br />\r\n&bull; C&aacute;ch thức l&agrave;m bạn, kết nối với thế hệ sống ảo v&agrave; biến họ th&agrave;nh những kh&aacute;ch h&agrave;ng trung th&agrave;nh của họ.</p>\r\n\r\n<h1>Ai n&ecirc;n đọc cuốn s&aacute;ch n&agrave;y</h1>\r\n\r\n<p>&bull; Chủ doanh nghiệp, startup, những người l&agrave;m marketing, kinh doanh Online, chuy&ecirc;n vi&ecirc;n nghi&ecirc;n cứu thị trường, nh&agrave; ph&aacute;t triển sản phẩm&hellip;<br />\r\n&bull; C&aacute;c bậc phụ huynh c&oacute; con sinh trong giai đoạn 1996 &ndash; 2012.<br />\r\n&bull; Những bạn trẻ thuộc thế hệ Gen Z mong muốn được kh&aacute;m ph&aacute; v&agrave; thấu hiểu bản th&acirc;n.</p>\r\n\r\n<h1>Mục lục</h1>\r\n\r\n<p><strong>LỜI MỞ ĐẦU</strong><br />\r\n<strong>LỜI TỰA</strong><br />\r\n<strong>LỜI GIỚI THIỆU</strong><br />\r\n<strong>PHẦN 1</strong>: Kết nối<br />\r\n<strong>PHẦN 2</strong>: Giải tr&iacute;<br />\r\n<strong>PHẦN 3</strong>: Học tập<br />\r\n<strong>PHẦN 4</strong>: C&ocirc;ng việc<br />\r\n<strong>PHẦN 5</strong>: Mua sắm<br />\r\n<strong>PHẦN 6</strong>: Di chuyển<br />\r\n<strong>PHẦN 7</strong>: Ăn uống<br />\r\n<strong>PHẦN 8</strong>: Thời trang</p>\r\n', '209000', 5, 0, 5, 'ub1ksfhn.png', '2020-11-10', 'admin', '2020-12-10', 'admin', 1, 20, 13),
(36, 'Marketing giỏi phải kiếm được tiền', '<p>Marketing l&agrave; một khoản đầu tư hay chi ph&iacute;?</p>\r\n\r\n<p>Marketing l&agrave; tạo ra quảng c&aacute;o thắng giải hay b&aacute;n h&agrave;ng?</p>\r\n\r\n<p>Marketing l&agrave; một hoạt động &ldquo;b&iacute; ẩn&rdquo;, &ldquo;nghệ thuật&rdquo;, &ldquo;s&aacute;ng tạo&rdquo; v&agrave; người l&agrave;m marketing phải c&oacute; tố chất kh&aacute;c người?</p>\r\n\r\n<p>Trong cuốn s&aacute;ch &ldquo;Marketing giỏi phải kiếm được tiền&rdquo; Sergio Zyman sẽ &rdquo;dạy&rdquo; cho cả thế giới biết Marketing thực sự c&oacute; nghĩa g&igrave;?</p>\r\n\r\n<h2>HAI VẤN ĐỀ CỦA MARKETING NG&Agrave;Y NAY L&Agrave;:</h2>\r\n\r\n<p>&ndash; Tại hầu hết c&aacute;c c&ocirc;ng ty, marketing đang kh&ocirc;ng hiệu quả v&agrave; do đ&oacute; bị coi l&agrave; một hoạt động kh&ocirc;ng cần thiết. Bất cứ khi n&agrave;o ng&acirc;n s&aacute;ch bị siết chặt, marketing l&agrave; một trong nhưng thứ đầu ti&ecirc;n bị cắt đi ch&iacute;nh bởi suy nghĩ rằng n&oacute; l&agrave; chi ph&iacute;.</p>\r\n\r\n<p>&ndash; Nhiều năm qua những người l&agrave;m marketing đ&atilde; t&aacute;i định vị v&agrave; n&acirc;ng cấp marketing th&agrave;nh nghệ thuật. Họ bị l&uacute;ng t&uacute;ng bởi vẻ h&agrave;o nho&aacute;ng, những giải thưởng, những buổi diễn thuyết, bay tới bay lui để chụp ảnh tr&ecirc;n c&aacute;c quần đảo nhiệt đới, v&agrave; họ qu&ecirc;n mất c&ocirc;ng việc của Marketer l&agrave; B&Aacute;N H&Agrave;NG.\\</p>\r\n\r\n<p>Họ n&oacute;i với nhau rằng c&aacute;c kết quả của hoạt động marketing l&agrave; kh&ocirc;ng thể đo đếm trong ng&agrave;y một ng&agrave;y hai được, chủ doanh nghiệp phải đo hiệu quả của marketing ở th&igrave; tương lai. C&ograve;n tương lai l&agrave; bao nhi&ecirc;u năm th&igrave; kh&ocirc;ng ai c&oacute; thể trả lời r&otilde; r&agrave;ng.</p>\r\n\r\n<p>Những c&aacute; nh&acirc;n n&agrave;y, người sắm vai &ocirc;ng chủ kh&oacute; t&iacute;nh, kẻ đ&oacute;ng vai nghệ sĩ ve sầu mải m&ecirc; ca h&aacute;t nhảy m&uacute;a tất cả đ&atilde; tạo n&ecirc;n một Marketing bất lực &ndash; Một Marketing&nbsp;<strong>KH&Ocirc;NG B&Aacute;N ĐƯỢC H&Agrave;NG, KH&Ocirc;NG KIẾM ĐƯỢC TIỀN!</strong></p>\r\n\r\n<p>Đ&oacute; l&agrave; l&yacute; do Sergio Zyman &ndash; Cựu Gi&aacute;m đốc Marketing của Coca Cola, một marketer l&atilde;o l&agrave;ng viết n&ecirc;n cuốn&nbsp;<strong>&ldquo;Marketing Giỏi Phải Kiếm Được Tiền&rdquo;</strong>. Cuốn s&aacute;ch l&agrave; tổng hợp của những thập kỷ học tập của Sergio Zyman để cho ra đời những chiến dịch, chiến thuật v&agrave; quy tr&igrave;nh v&ocirc; c&ugrave;ng ngoạn mục. M ột cuốn s&aacute;ch m&agrave; khi lật giở đến trang cuối c&ugrave;ng bạn sẽ hiểu được Marketing thực sự l&agrave; g&igrave;, l&agrave;m thế n&agrave;o để Marketing hiệu quả v&agrave; kiếm được tiền!</p>\r\n\r\n<p>Rằng,&nbsp;<strong>marketing l&agrave; một khoản đầu tư gi&aacute; trị</strong>&nbsp;nhất của một doanh nghiệp, gi&uacute;p doanh nghiệp gia tăng về mặt gi&aacute; trị thương hiệu cũng như kiếm được nhiều tiền v&igrave; vậy chỉ c&oacute; thể tăng đầu tư hoặc giữ nguy&ecirc;n chứ kh&ocirc;ng n&ecirc;n cắt giảm.</p>\r\n\r\n<p>Nếu điều c&ocirc;ng ty bạn l&agrave;m khi kh&ocirc;ng b&aacute;n được h&agrave;ng l&agrave; cắt giảm ng&acirc;n s&aacute;ch Marketing c&agrave;ng nhiều c&agrave;ng tốt th&igrave; bạn toi rồi!</p>\r\n\r\n<p>V&agrave; những người l&agrave;m Marketing cần nhớ lại thật nhanh rằng&nbsp;<strong>marketing l&agrave; b&aacute;n sản phẩm.</strong>&nbsp;Marketing kh&ocirc;ng phải l&agrave; một tr&ograve; chơi v&agrave; n&oacute; cũng chẳng phải nghệ thuật trang tr&iacute; kỳ diệu n&agrave;o cả. N&oacute; l&agrave; c&ocirc;ng chuyện kinh doanh, l&agrave;m ăn thuần t&uacute;y. Marketing ch&iacute;nh l&agrave; việc lập ra những kế hoạch một c&aacute;ch thấu đ&aacute;o, c&oacute; hệ thống v&agrave; thực thi hiệu quả những h&agrave;nh động nhằm l&ocirc;i k&eacute;o th&ecirc;m nhiều người mua nhiều sản phẩm của bạn một thường xuy&ecirc;n hơn gi&uacute;p c&ocirc;ng ty l&agrave;m ra th&ecirc;m tiền.</p>\r\n\r\n<p><strong>Ch&iacute;nh v&igrave; lẽ đ&oacute;, Marketing trong tương lai &ndash; l&agrave; marketing-quay-về-nền-tảng-cơ-bản. Bạn chi tiền để kiếm ra tiền!</strong></p>\r\n\r\n<p><strong>VỀ CUỐN S&Aacute;CH:</strong></p>\r\n\r\n<p>&ndash;&nbsp;<strong>&ldquo;Marketing Giỏi Phải Kiếm Được Tiền&rdquo;</strong>&nbsp;(Tựa gốc: The End Of Advertising As We Know It) l&agrave; nền tảng cơ bản ng&agrave;y ng&agrave;y soi s&aacute;ng v&agrave; l&agrave; kim chỉ nam cho tất cả c&aacute;c hoạt động marketing &ndash; tiếp thị của nhiều doanh nghiệp tr&ecirc;n to&agrave;n thế giới.</p>\r\n\r\n<p>&ndash; S&aacute;ch được được t&aacute;i bản 20 lần bằng 19 thứ tiếng. V&agrave; được đ&aacute;nh gi&aacute; 4/5 sao tại Amazon.</p>\r\n\r\n<h2>VỀ T&Aacute;C GIẢ:</h2>\r\n\r\n<p>&ndash; Sergio Zyman &ndash; Cựu Gi&aacute;m đốc Marketing của Coca Cola, một marketer l&atilde;o l&agrave;ng c&oacute; tầm ảnh hưởng lớn. Người m&agrave;, cả triệu marketers chỉ c&oacute; được 1-2 người xuất ch&uacute;ng như thế.</p>\r\n\r\n<p>&ndash; Người đ&agrave;n &ocirc;ng c&oacute; biệt hiệu &ldquo;Aya-Cola&rdquo; tại đại lộ Madison, New York nhờ việc tăng gi&aacute; trị thị trường của Coca Cola từ 56 tỷ đ&ocirc; la năm 1993 đến 193 tỷ đ&ocirc; (tăng trưởng 245% trong vỏn vẹn 5 năm).</p>\r\n\r\n<p>&ndash; S&aacute;ng lập v&agrave; l&agrave; CEO của Z Marketing, một c&ocirc;ng ty chiến lược marketing đa dạng tập trung v&agrave;o việc th&uacute;c đẩy marketing đẳng cấp thế giới.</p>\r\n\r\n<h2><strong>AI CẦN MARKETING GIỎI ĐỂ KIẾM ĐƯỢC TIỀN?</strong></h2>\r\n\r\n<p>Nếu bạn l&agrave; chủ doanh nghiệp, hoặc nếu bạn l&agrave; quản l&yacute; marketing, những người l&agrave;m marketing hoặc l&agrave; người mới khởi nghiệp hoặc chỉ l&agrave; một người y&ecirc;u th&iacute;ch muốn hiểu marketing l&agrave; g&igrave;, th&igrave; bạn sẽ kh&ocirc;ng ph&iacute; phạm bất cứ một gi&acirc;y ph&uacute;t n&agrave;o khi bạn mua, đọc để t&igrave;m hiểu cuốn</p>\r\n', '287000', 4, 0, 5, 'zelbc4r9.png', '2020-11-10', 'admin', '2020-12-10', 'admin', 1, 22, 13),
(37, '66 ngày thử thách', '<p><strong>66 NG&Agrave;Y THỬ TH&Aacute;CH&nbsp; &ndash; Đ&Aacute;NH BẠI CON QUỶ LƯỜI BIẾNG TRONG BẠN</strong></p>\r\n\r\n<p>Mọi người hay n&oacute;i vui với nhau rằng:&nbsp;<em>Chẳng ai chết đuối trong mồ h&ocirc;i của ch&iacute;nh m&igrave;nh m&agrave; chỉ chết ch&igrave;m trong sự lười biếng.</em>&nbsp;Mồ h&ocirc;i l&agrave; ph&eacute;p ẩn dụ cho cố gắng của bạn khi l&agrave;m việc. Tuy nhi&ecirc;n, mồ h&ocirc;i đổ ra khi bạn chạy 100m v&agrave; 10km rất kh&aacute;c nhau, bạn bỏ ra c&ocirc;ng sức c&agrave;ng nhiều, th&agrave;nh quả đạt được c&agrave;ng r&otilde; rệt. Nhưng d&ugrave; mồ h&ocirc;i c&oacute; đổ nhiều thế n&agrave;o đi nữa, n&oacute; vẫn kh&ocirc;ng thể nhấn ch&igrave;m bạn. Lười biếng th&igrave; ngược lại, bạn c&agrave;ng bu&ocirc;ng xu&ocirc;i m&igrave;nh th&igrave; bản th&acirc;n c&agrave;ng dễ l&uacute;n s&acirc;u v&agrave;o vũng lầy lười nh&aacute;c, l&acirc;u dần, bạn trở th&agrave;nh người sống t&ugrave;y &yacute;, kh&ocirc;ng c&oacute; &yacute; ch&iacute; v&agrave; nghị lực để l&agrave;m bất cứ việc g&igrave;.</p>\r\n\r\n<p>Kh&ocirc;ng ai th&iacute;ch bị người kh&aacute;c gọi l&agrave; người lười nh&aacute;c cả, ch&uacute;ng ta sẽ c&oacute; t&acirc;m l&yacute; tự vệ v&agrave; t&igrave;m mu&ocirc;n v&agrave;n l&yacute; do để giải th&iacute;ch rằng: tại sao t&ocirc;i kh&ocirc;ng học ngoại ngữ, tại sao t&ocirc;i kh&ocirc;ng thể dậy sớm tập thể dục, tại sao t&ocirc;i kh&ocirc;ng để d&agrave;nh đủ tiền để đi du lịch,&hellip; Thực chất, ch&uacute;ng ta lu&ocirc;n c&oacute; nhiều việc &ldquo;tr&ecirc;n trời rơi xuống&rdquo; khiến kế hoạch của m&igrave;nh giữa đường g&atilde;y g&aacute;nh hoặc cảm x&uacute;c bị tr&ocirc;i tuột trong v&agrave;i ng&agrave;y sau ph&aacute;t biểu h&ugrave;ng hồn của bạn (khi muốn thực hiện một kế hoạch lớn lao n&agrave;o đ&oacute;). Điều đ&oacute; khiến bạn thất vọng với bản th&acirc;n, xem lời n&oacute;i của m&igrave;nh &ldquo;chẳng ra g&igrave;&rdquo;. L&acirc;u dần, bất cứ kế hoạch hay quyết t&acirc;m bạn đặt ra đều rất dễ thất bại.</p>\r\n\r\n<p>C&aacute;i bạn thiếu l&agrave; sự nghi&ecirc;m t&uacute;c, kỷ luật v&agrave; h&agrave;o hứng khi bắt đầu x&acirc;y dựng một th&oacute;i quen tốt. Đ&oacute; ch&iacute;nh l&agrave; l&iacute; do khiến ch&uacute;ng t&ocirc;i &ndash; Happy Live Team, muốn d&agrave;nh tặng bạn một quyển sổ tay thực h&agrave;nh&nbsp;<strong>&ldquo;66 ng&agrave;y ng&agrave;y thử th&aacute;ch&rdquo;</strong>. Như một người bạn đồng h&agrave;nh v&agrave; chia sẻ kh&oacute; khăn của bạn trong qu&aacute; tr&igrave;nh tạo lập một th&oacute;i quen.</p>\r\n\r\n<p>&ldquo;66 ng&agrave;y thử th&aacute;ch&rdquo; l&agrave; sổ tay thực h&agrave;nh gi&uacute;p bạn tạo dựng một th&oacute;i quen bằng việc l&agrave;m đi lặp lại n&oacute; trong 66 ng&agrave;y li&ecirc;n tục kh&ocirc;ng ngắt qu&atilde;ng. 66 ng&agrave;y thử th&aacute;ch gợi &yacute; cho bạn một kế hoạch thử th&aacute;ch ch&iacute;nh m&igrave;nh ở 6 lĩnh vực ch&iacute;nh cần quan t&acirc;m trong cuộc sống như: Thể chất, cuộc sống c&aacute; nh&acirc;n, mối quan hệ, t&agrave;i ch&iacute;nh c&aacute; nh&acirc;n, c&ocirc;ng việc, kinh doanh v&agrave; t&acirc;m linh. Bạn c&oacute; thể khoanh v&ugrave;ng th&oacute;i quen m&igrave;nh đang ấp ủ v&agrave; l&ecirc;n kế hoạch thực hiện n&oacute; v&agrave; theo d&otilde;i việc thực h&agrave;nh mỗi ng&agrave;y bằng c&aacute;ch ghi ch&eacute;p v&agrave;o sổ. Cuối mỗi tuần thử th&aacute;ch, bạn sẽ nhận được những kh&iacute;ch lệ, đ&aacute;nh gi&aacute; về những g&igrave; bạn đ&atilde; l&agrave;m, những thử th&aacute;ch bạn vượt qua.</p>\r\n\r\n<h3><strong>NHƯNG TẠI SAO LẠI L&Agrave; 66 NG&Agrave;Y M&Agrave; KH&Ocirc;NG PHẢI L&Agrave; 33, 99 HAY 7X7=49 NG&Agrave;Y?</strong></h3>\r\n\r\n<p>Theo nhiều nghi&ecirc;n cứu, 66 ng&agrave;y l&agrave; khoảng thời gian tuyệt vời để bạn h&igrave;nh th&agrave;nh v&agrave; quen với việc sống c&ugrave;ng th&oacute;i quen mới. Sau 66 ng&agrave;y, th&oacute;i quen ấy ch&iacute;nh thức trở th&agrave;nh một phần cuộc sống của bạn, bạn sẽ kh&ocirc;ng thấy mệt mỏi hay kh&oacute; chịu khi thực hiện th&oacute;i quen, m&agrave; xem đ&oacute; l&agrave; một việc hiển nhi&ecirc;n, cần thiết v&agrave; kh&ocirc;ng thể thiếu trong cuộc sống của bạn.</p>\r\n\r\n<p>Bạn sẽ th&agrave;nh c&ocirc;ng trong việc x&acirc;y dựng th&oacute;i quen mới khi nghi&ecirc;m t&uacute;c thực hiện thử th&aacute;ch 66 ng&agrave;y (li&ecirc;n tục v&agrave; kh&ocirc;ng ngắt qu&atilde;ng).</p>\r\n\r\n<h3><strong>NGO&Agrave;I BẠN RA, AI L&Agrave; NGƯỜI PH&Ugrave; HỢP VỚI THỬ TH&Aacute;CH 66 NG&Agrave;Y?&nbsp;</strong>&ndash; Những người đang cần x&acirc;y dựng những th&oacute;i quen tốt nhưng chưa c&oacute; giải ph&aacute;p v&agrave; động lực để thực hiện.</h3>\r\n\r\n<p>&ndash; Những người muốn chiến thắng sự lười biếng v&agrave; trở th&agrave;nh phi&ecirc;n bản tốt hơn của ch&iacute;nh m&igrave;nh</p>\r\n\r\n<p>&ndash; Những ai muốn muốn gi&agrave;u c&oacute;, th&agrave;nh c&ocirc;ng v&agrave; hạnh ph&uacute;c.</p>\r\n\r\n<h3><strong>KHI Đ&Atilde; THỰC HIỆN TH&Agrave;NH C&Ocirc;NG THỬ TH&Aacute;CH 66 NG&Agrave;Y, BẠN N&Ecirc;N L&Agrave;M G&Igrave;?&nbsp;</strong></h3>\r\n\r\n<p>1.&nbsp;Chia sẻ 66 ng&agrave;y của bạn ở chế độ c&ocirc;ng khai tr&ecirc;n facebook k&egrave;m hashtag #66ngaythuthach</p>\r\n\r\n<p>2. Truyền cảm hứng cho những người xung quanh, để họ thấy rằng, bạn l&agrave;m được th&igrave; họ cũng sẽ l&agrave;m được.</p>\r\n\r\n<p>3. Tiếp tục theo đuổi c&aacute;c th&oacute;i quen mới v&agrave; duy tr&igrave; th&oacute;i quen cũ.</p>\r\n\r\n<p><em><strong>&ldquo;Đừng sợ v&agrave; đừng lười biếng. Đừng để hối tiếc &aacute;m ảnh tương lai. H&atilde;y l&ecirc;n đường. C&aacute;c bạn kh&ocirc;ng c&oacute; g&igrave; để mất ngo&agrave;i sự tr&igrave; trệ.&rdquo; &ndash; TS.Alan Phan</strong></em></p>\r\n', '89000', 2, 0, 5, 'zsduryh6.png', '2020-11-10', 'admin', '2020-12-10', 'admin', 1, 2, 14),
(38, 'Làm chủ tuổi 20', '<p><strong>L&agrave;m chủ tuổi 20</strong>&nbsp;được viết lại dựa tr&ecirc;n những trải nghiệm trong cuộc sống của t&aacute;c giả Dương Duy B&aacute;ch &ndash; một người sớm tự lập v&agrave; đạt được nhiều th&agrave;nh c&ocirc;ng ở tuổi 20. Ngo&agrave;i c&acirc;u chuyện của ch&iacute;nh m&igrave;nh, t&aacute;c giả c&ograve;n ghi lại những b&agrave;i học m&agrave; anh học được từ những người trẻ th&agrave;nh c&ocirc;ng kh&aacute;c v&agrave; ph&acirc;n t&iacute;ch l&yacute; do kh&aacute;ch quan khiến họ đạt được mục ti&ecirc;u&nbsp;khi tuổi đời c&ograve;n rất trẻ.</p>\r\n\r\n<p>Xuy&ecirc;n suốt hai mươi chương s&aacute;ch l&agrave; những chia sẻ về kh&aacute;m ph&aacute; đam m&ecirc;, l&agrave;m chủ th&oacute;i quen v&agrave; vận mệnh: từ tho&aacute;t khỏi sợi d&acirc;y xiềng x&iacute;ch, học c&aacute;ch chịu tr&aacute;ch nhiệm 100% với cuộc sống của m&igrave;nh đến học c&aacute;ch y&ecirc;u thương, biết ơn, độc lập t&agrave;i ch&iacute;nh, nỗ lực th&agrave;nh c&ocirc;ng nhanh hơn tốc độ gi&agrave; đi của bố mẹ,&hellip;</p>\r\n\r\n<p>Điều tạo n&ecirc;n sự kh&aacute;c biệt giữa<strong>&nbsp;L&agrave;m chủ tuổi 20</strong>&nbsp;với c&aacute;c cuốn s&aacute;ch ph&aacute;t triển bản th&acirc;n kh&aacute;c l&agrave; t&aacute;c giả đ&atilde; d&agrave;nh gần ba năm để chia sẻ những g&igrave; viết trong cuốn s&aacute;ch n&agrave;y đến h&agrave;ng chục ngh&igrave;n bạn trẻ, th&ocirc;ng qua c&aacute;c buổi n&oacute;i chuyện v&agrave; chương tr&igrave;nh đ&agrave;o tạo. N&oacute;i đ&uacute;ng hơn, đ&acirc;y l&agrave; cuốn s&aacute;ch được viết bởi một người Việt Nam trẻ tuổi đ&atilde; ứng dụng những b&agrave;i học, kiến thức n&agrave;y th&agrave;nh c&ocirc;ng để thay đổi cuộc sống của m&igrave;nh v&agrave; h&agrave;ng ngh&igrave;n bạn trẻ kh&aacute;c.</p>\r\n\r\n<p>Cứ đi ắt sẽ đến, cứ đọc rồi ứng dụng, bạn sẽ thấy cuộc sống m&igrave;nh thay đổi đ&aacute;ng kể. Nỗ lực thay đổi cuộc sống kh&ocirc;ng phải nỗ lực tạo ra điều g&igrave; lớn lao, m&agrave; l&agrave; ki&ecirc;n tr&igrave; với những h&agrave;nh động t&iacute;ch cực, suy nghĩ t&iacute;ch cực h&agrave;ng ng&agrave;y, h&agrave;ng giờ, h&agrave;ng ph&uacute;t, h&agrave;ng gi&acirc;y. V&igrave;:&nbsp;<strong>C&aacute;ch bạn l&agrave;m một việc ch&iacute;nh l&agrave; c&aacute;ch bạn l&agrave;m mọi việc.</strong></p>\r\n\r\n<h2>VỀ T&Aacute;C GIẢ</h2>\r\n\r\n<p>T&aacute;c giả&nbsp;<strong>Dương Duy B&aacute;ch</strong>&nbsp;hiện l&agrave; nh&agrave; s&aacute;ng lập c&ocirc;ng ty gi&aacute;o dục v&agrave; tổ chức sự kiện FLY Việt Nam. Anh c&ograve;n được biết đến l&agrave; một diễn giả trẻ, th&agrave;nh c&ocirc;ng từ rất sớm tại Việt Nam. Anh nổi tiếng bởi th&agrave;nh t&iacute;ch khởi nghiệp đ&aacute;ng nể v&agrave; những hoạt động ph&aacute;t triển bản th&acirc;n cho giới trẻ.</p>\r\n\r\n<h2>HƠN 20 TUỔI C&Oacute; ĐỌC ĐƯỢC CUỐN S&Aacute;CH N&Agrave;Y?&nbsp;</h2>\r\n\r\n<p>Khi bắt tay v&agrave;o viết cuốn s&aacute;ch n&agrave;y, t&aacute;c giả đ&atilde; bắt gặp những c&acirc;u hỏi như tuổi 20 c&ograve;n qu&aacute; trẻ để học c&aacute;ch l&agrave;m chủ hay những lời than v&atilde;n rằng m&igrave;nh c&ograve;n qu&aacute; trẻ, hoặc qu&aacute; gi&agrave; để đọc L&agrave;m chủ tuổi 20. Tuổi 20 chỉ l&agrave; một c&aacute;ch gọi để bạn li&ecirc;n tưởng đến tuổi trẻ, tuổi ước mơ v&agrave; dấn th&acirc;n để đạt mục ti&ecirc;u của m&igrave;nh.</p>\r\n\r\n<p><strong>Nếu bạn đang l&agrave; một người 16 &ndash; 30 tuổi</strong>, rất hoan ngh&ecirc;nh, bạn ho&agrave;n to&agrave;n ph&ugrave; hợp với quyển s&aacute;ch n&agrave;y. Người &ldquo;chưa đủ tuổi&rdquo; c&oacute; thể t&igrave;m hiểu v&agrave; thiết lập cho m&igrave;nh tư duy t&iacute;ch cực ngay từ sớm. Người &ldquo;qu&aacute; tuổi&rdquo; vẫn c&oacute; thể đọc để tự vấn lại bản th&acirc;n v&agrave; thay đổi trước khi qu&aacute; muộn.</p>\r\n\r\n<p><strong>Nhưng nếu bạn đ&atilde; &ldquo;lỡ&rdquo; tr&ecirc;n 30 th&igrave; thế n&agrave;o?</strong>&nbsp;Bạn vẫn c&oacute; thể khuyến kh&iacute;ch em/con/ch&aacute;u m&igrave;nh t&igrave;m đọc. V&igrave; đ&acirc;y l&agrave; một quyển s&aacute;ch được ghi lại từ một người trẻ th&agrave;nh c&ocirc;ng từ rất sớm v&agrave; kiến thức trong quyển s&aacute;ch n&agrave;y đ&atilde; được h&agrave;ng ngh&igrave;n bạn trẻ ứng dụng th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<h2>MỤC LỤC:</h2>\r\n\r\n<p><strong>LỜI TỰA</strong><br />\r\n<strong>LỜI GIỚI THIỆU</strong><br />\r\n<strong>LỜI CẢM ƠN</strong><br />\r\n<strong>LỜI MỞ ĐẦU</strong><br />\r\n<strong>CHƯƠNG 1:</strong>&nbsp;Chặt đứt sợi d&acirc;y v&ocirc; h&igrave;nh<br />\r\n<strong>CHƯƠNG 2:</strong>&nbsp;Nghĩ lớn<br />\r\n<strong>CHƯƠNG 3:</strong>&nbsp;Đam m&ecirc; v&agrave; mục đ&iacute;ch cuộc đời<br />\r\n<strong>CHƯƠNG 4:</strong>&nbsp;Lu&ocirc;n lu&ocirc;n học hỏi<br />\r\n<strong>CHƯƠNG 5:</strong>&nbsp;H&atilde;y lu&ocirc;n vui vẻ<br />\r\n<strong>CHƯƠNG 6:</strong>&nbsp;Duy tr&igrave; nguồn năng lượng đỉnh cao<br />\r\n<strong>CHƯƠNG 7:</strong>&nbsp;Quản l&yacute; tốt thời gian của bạn<br />\r\n<strong>CHƯƠNG 8:</strong>&nbsp;L&agrave;m chủ mối quan hệ<br />\r\n<strong>CHƯƠNG 9:</strong>&nbsp;Những điều nhỏ nhưng quan trọng<br />\r\n<strong>CHƯƠNG 10:</strong>&nbsp;Đứng tr&ecirc;n đ&ocirc;i ch&acirc;n của m&igrave;nh<br />\r\n<strong>CHƯƠNG 11:</strong>&nbsp;L&agrave;m tới đi<br />\r\n<strong>CHƯƠNG 12:</strong>&nbsp;Ho&agrave;n thiện bản th&acirc;n<br />\r\n<strong>CHƯƠNG 13:</strong>&nbsp;H&atilde;y kh&aacute;c biệt<br />\r\n<strong>CHƯƠNG 14:</strong>&nbsp;H&atilde;y sống khi&ecirc;m nhường v&agrave; sẵn s&agrave;ng hợp t&aacute;c<br />\r\n<strong>CHƯƠNG 15:</strong>&nbsp;Gieo nh&acirc;n n&agrave;o gặt quả đấy<br />\r\n<strong>CHƯƠNG 16:</strong>&nbsp;Sống trong thế giới biết ơn<br />\r\n<strong>CHƯƠNG 17:</strong>&nbsp;Th&agrave;nh c&ocirc;ng nhanh hơn tốc độ gi&agrave; đi của bố mẹ<br />\r\n<strong>CHƯƠNG 18:</strong>&nbsp;Y&ecirc;u thương<br />\r\n<strong>CHƯƠNG 19:</strong>&nbsp;Bay l&ecirc;n n&agrave;o<br />\r\n<strong>CHƯƠNG 20:</strong>&nbsp;Đừng bao giờ từ bỏ ước mơ<br />\r\n<strong>LỜI KẾT</strong><br />\r\n<strong>PHỤ CHƯƠNG:</strong>&nbsp;Một v&agrave;i suy nghĩ về sự nghiệp<br />\r\n<strong>PHỤ CHƯƠNG:</strong>&nbsp;Mọi người n&oacute;i g&igrave; về chương tr&igrave;nh L&agrave;m chủ tuổi 20</p>\r\n', '201000', 5, 0, 10, 'c50qb3nr.png', '2020-11-10', 'admin', '2020-12-10', 'admin', 1, 26, 14),
(39, 'Thiết kế cuộc đời thịnh vượng', '<p>Anh Th&aacute;i Phạm, Founder Cộng đồng Happy Live &ndash; Đầu tư t&agrave;i ch&iacute;nh v&agrave; thịnh vượng, nguy&ecirc;n l&agrave; Gi&aacute;m đốc Marketing của Vinamilk với 13 năm kinh nghiệm trong việc x&acirc;y dựng thương hiệu, marketing v&agrave; ph&aacute;t triển kinh doanh. Anh được mọi người biết đến như một nh&agrave; đầu tư, một doanh nh&acirc;n đam m&ecirc; gi&aacute;o dục, huấn luyện v&agrave; viết l&aacute;ch.</p>\r\n\r\n<p>Anh cũng l&agrave; bi&ecirc;n dịch, dịch giả của kh&aacute; nhiều cuốn s&aacute;ch về đầu tư &amp; marketing b&aacute;n chạy như: Ng&agrave;y đ&ograve;i nợ, L&agrave;m gi&agrave;u từ chứng kho&aacute;n, Nghệ thuật đầu tư Dhandho, Lột x&aacute;c để trở th&agrave;nh nh&agrave; đầu tư gi&aacute; trị, Marketing giỏi phải kiếm được tiền, Hệ thống b&aacute;n h&agrave;ng đỉnh cao, Nuốt c&aacute; lớn&hellip;</p>\r\n\r\n<p>Ngo&agrave;i ra, k&ecirc;nh youtube Th&aacute;i Phạm với tinh thần: thay đổi cuộc sống bằng c&aacute;ch gi&uacute;p mọi người trở n&ecirc;n gi&agrave;u c&oacute; hơn, cũng nhận được gần 200.000 lượt người đăng k&yacute; quan t&acirc;m v&agrave; theo d&otilde;i.</p>\r\n\r\n<h2><strong>THIẾT KẾ&nbsp;CUỘC ĐỜI THỊNH VƯỢNG &ndash;&nbsp;HƯỚNG DẪN CHI TIẾT C&Aacute;CH X&Acirc;Y DỰNG&nbsp;CUỘC ĐỜI Đ&Aacute;NG MƠ ƯỚC CỦA BẠN</strong></h2>\r\n\r\n<p>C&oacute; thể n&oacute;i, đ&acirc;y vừa l&agrave; một quyển tự truyện đi k&egrave;m với những b&iacute; quyết tạo n&ecirc;n một cuộc đời đ&aacute;ng sống trong suốt 20 năm &ldquo;lăn lộn&rdquo; trường đời của t&aacute;c giả.&nbsp;</p>\r\n\r\n<p>Quyển s&aacute;ch&nbsp;kh&ocirc;ng ru ngủ bạn với những c&ocirc;ng thức thay đổi bản th&acirc;n thần th&aacute;nh&nbsp;m&agrave; tập trung v&agrave;o giải quyết những vấn đề của bạn bằng c&aacute;ch đưa ra c&aacute;c phương ph&aacute;p đ&aacute;nh gi&aacute;, nh&igrave;n nhận bản th&acirc;n v&agrave; cung cấp cho bạn những &ldquo;vật tư&rdquo; x&acirc;y n&ecirc;n một cuộc đời bạn mong muốn.&nbsp;</p>\r\n\r\n<p>Bởi trước&nbsp;khi x&acirc;y tr&ecirc;n nền đất mới những c&ocirc;ng tr&igrave;nh ki&ecirc;n cố, bạn phải đập bỏ những suy nghĩ cũ r&iacute;ch &ndash; những thứ vẫn đang giữ ch&acirc;n bạn với sự tầm thường. Bạn kh&ocirc;ng thể x&acirc;y n&ecirc;n một ng&ocirc;i nh&agrave; đẹp v&agrave; vững chắc từ một nền nh&agrave; k&eacute;m chất lượng. Những&nbsp;vi&ecirc;n gạch k&eacute;m chất lượng m&agrave; bạn cần đập bỏ l&agrave; những th&agrave;nh kiến về người gi&agrave;u, những suy nghĩ sai lệch về th&agrave;nh c&ocirc;ng như chỉ c&oacute; học đại học l&agrave; con đường duy nhất để đạt được mục ti&ecirc;u, ai th&agrave;nh c&ocirc;ng cũng đều hạnh ph&uacute;c, theo đuổi đam m&ecirc;, tiền sẽ tới,&hellip; Những điều nghe c&oacute; vẻ hay, c&oacute; vẻ ổn nhưng hệ quả của những suy nghĩ đ&oacute; với cuộc sống của bạn kh&ocirc;ng ổn ch&uacute;t n&agrave;o.&nbsp;</p>\r\n\r\n<p>Người th&ocirc;ng minh&nbsp;l&agrave; người học được nhiều từ&nbsp;sai lầm của m&igrave;nh,&nbsp;c&ograve;n&nbsp;người th&ocirc;ng th&aacute;i&nbsp;l&agrave; người học được nhiều b&agrave;i học từ&nbsp;th&agrave;nh c&ocirc;ng v&agrave; thất bại của người kh&aacute;c.</p>\r\n\r\n<h3>&ndash;&nbsp;Tr&iacute;ch từ s&aacute;ch&nbsp;Thiết kế cuộc đời thịnh vượng&nbsp;&ndash;</h3>\r\n\r\n<p>Chẳng&nbsp;ai muốn đ&aacute;nh đổi một khoảng đời tuổi trẻ cho những việc l&agrave;m v&ocirc; nghĩa v&agrave; cũng kh&ocirc;ng ai muốn d&agrave;nh thời gian đ&oacute; để học từ những sai lầm m&agrave; đ&aacute;ng ra, nếu sở hữu được những nguồn tri thức v&ocirc; gi&aacute;, quen biết người từng trải ch&uacute;ng ta sẽ dễ d&agrave;ng tr&aacute;nh được những sai lầm đ&oacute; (v&agrave; học được những b&agrave;i học &ldquo;cao cấp hơn&rdquo; chẳng hạn).</p>\r\n\r\n<p>Bạn th&iacute;ch l&agrave;m người th&ocirc;ng minh hay l&agrave; người th&ocirc;ng th&aacute;i?&nbsp;<strong>Nếu l&agrave; người th&ocirc;ng th&aacute;i, &ldquo;cẩm nang&rdquo; Thiết kế cuộc đời thịnh vượng c&oacute; thể gi&uacute;p bạn:&nbsp;</strong></p>\r\n\r\n<ul>\r\n	<li>Nhận ra những&nbsp;<strong>quan điểm sai lầm</strong>&nbsp;trong qu&aacute; khứ</li>\r\n	<li>Tạo n&ecirc;n một&nbsp;<strong>hệ nhận thức mới</strong>, t&igrave;m kiếm lại đam m&ecirc; v&agrave; mục đ&iacute;ch cuộc đời bạn</li>\r\n	<li>X&acirc;y dựng tầm nh&igrave;n cuộc sống v&agrave;&nbsp;<strong>thiết kế một bản vẽ cho tương lai gần</strong></li>\r\n	<li><strong>N&acirc;ng cao năng suất lao động</strong>&nbsp;th&ocirc;ng qua r&egrave;n luyện khả năng &ldquo;si&ecirc;u tập trung&rdquo;, quản l&yacute; thời gian, x&aacute;c định v&ograve;ng tr&ograve;n quan t&acirc;m, v&ograve;ng tr&ograve;n ảnh hưởng trong c&ocirc;ng việc</li>\r\n	<li><strong>X&acirc;y dựng những mối quan hệ chất lượng</strong>&nbsp;bằng c&aacute;ch: nắm bắt nhu cầu của của người bạn quan t&acirc;m, những dạng ng&ocirc;n ngữ khiến kh&ocirc;ng ai c&oacute; thể từ chối giao tiếp với bạn, c&ocirc;ng thức &ldquo;si&ecirc;u kết nối&rdquo; 5 &ndash; 50 &ndash; 100 gi&uacute;p định hướng đ&acirc;u l&agrave; trọng t&acirc;m trong tất cả mối quan hệ của bạn</li>\r\n	<li><strong>Học c&aacute;ch quản l&yacute; t&agrave;i ch&iacute;nh c&aacute; nh&acirc;n</strong>: tự do t&agrave;i ch&iacute;nh l&agrave; g&igrave;, bao nhi&ecirc;u năm nữa bạn mới được tự do t&agrave;i ch&iacute;nh v&agrave; n&ecirc;n chi ti&ecirc;u thế n&agrave;o để biến ước mơ mua đồ kh&ocirc;ng nh&igrave;n gi&aacute;, nghỉ l&agrave;m nhưng vẫn kiếm ra tiền th&agrave;nh hiện thực,&hellip;? Tất cả sẽ được giải quyết trong chương cuối c&ugrave;ng của quyển s&aacute;ch n&agrave;y.&nbsp;</li>\r\n</ul>\r\n\r\n<p><em>&ldquo;Cuốn s&aacute;ch n&agrave;y, kh&ocirc;ng l&ecirc;n g&acirc;n, kh&ocirc;ng chỉ dạy, kh&ocirc;ng gi&aacute;o điều bạn bất cứ điều g&igrave;. N&oacute; được viết ra bởi trải nghiệm, bởi sự học tập kh&ocirc;ng ngừng nghỉ v&agrave; cả những thất bại, hay đơn giản l&agrave; sự trải nghiệm của t&ocirc;i n&ecirc;n t&ocirc;i tin n&oacute; thực hữu hiệu cho những người khao kh&aacute;t th&agrave;nh c&ocirc;ng.&nbsp;</em></p>\r\n\r\n<p><em>D&ugrave; bạn 17, hay bạn 20 hay bạn 30 thậm ch&iacute; 40 tuổi, nếu bạn muốn thay đổi v&agrave; bắt đầu một cuộc đời mới: T&ocirc;i xin ch&agrave;o đ&oacute;n bạn!&nbsp;</em></p>\r\n\r\n<p><em>Với tất cả t&acirc;m huyết được gửi gắm trong cuốn s&aacute;ch, t&ocirc;i chắc chắn rằng bạn sẽ th&agrave;nh c&ocirc;ng!&rdquo;&nbsp;</em></p>\r\n\r\n<p>&ndash; Th&aacute;i Phạm &ndash;&nbsp;</p>\r\n', '288000', 5, 0, 5, 'jv81a35k.png', '2020-11-10', 'admin', '2020-12-10', 'admin', 1, 24, 14),
